import React from 'react';
import { Route } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { LinkContainer } from 'react-router-bootstrap';
import { Container, Row, Col, Button, Form } from 'react-bootstrap';
import SearchBox from './SearchBox';
import { logout } from '../actions/userActions';

const Header = () => {
  const dispatch = useDispatch();

  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;

  const logoutHandler = () => {
    dispatch(logout());
  };

  return (
    <header>
      {/* Logo và thanh tìm kiếm */}
      <Container fluid className="py-3" style={{ backgroundColor: '#fff' }}>
        <Row className="align-items-center">
          {/* Logo */}
          <Col lg={3} md={4} sm={12} className="text-center text-md-left">
            <LinkContainer to="/">
              <div className="logo d-flex align-items-center">
                <img
                  src="/assets/img/logo/logo.png"
                  alt="Logo"
                  style={{ maxWidth: '80px', marginRight: '10px' }}
                />
                <span style={{ fontSize: '1.5rem', fontWeight: 'bold', color: '#000' }}>NGHIEN</span>
              </div>
            </LinkContainer>
          </Col>

          {/* Thanh tìm kiếm */}
          <Col lg={6} md={8} sm={12}>
            <Route
              render={({ history }) => (
                <div className="search-box d-flex">
                  <Form.Control
                    type="text"
                    placeholder="Tìm kiếm mọi thứ ở đây.."
                    className="mr-2"
                    style={{ border: '1px solid #ccc', borderRadius: '5px' }}
                  />
                  <Button
                    variant="warning"
                    style={{
                      color: '#000',
                      fontWeight: 'bold',
                      borderRadius: '5px',
                    }}
                  >
                    Tìm kiếm
                  </Button>
                </div>
              )}
            />
          </Col>
        </Row>
      </Container>
    </header>
  );
};

export default Header;
