import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Container, Row, Col } from 'react-bootstrap';
import ProductCarousel from '../components/ProductCarousel';
import Banner from '../components/Banner';
import Message from '../components/Message';
import Loader from '../components/Loader';
import Paginate from '../components/Paginate';
import Meta from '../components/Meta';
import { listProducts } from '../actions/productActions';


const FeatureSection = () => (
  <Container className="py-5">
    <Row className="text-center feature-section">
      {[
        {
          img: '/images/icon/icon-shipping.jpg',
          title: 'Miễn phí giao hàng',
          desc: 'Miễn phí vận chuyển tất cả các đơn hàng',
        },
        {
          img: '/images/icon/icon-support.jpg',
          title: 'Hỗ trợ 24/7',
          desc: 'Liên hệ bất kỳ lúc nào',
        },
        {
          img: '/images/icon/icon-refund.jpg',
          title: 'Hoàn tiền 100%',
          desc: 'Bạn có 15 ngày để quay lại',
        },
        {
          img: '/images/icon/icon-return.png',
          title: '30 ngày trả hàng',
          desc: 'Nếu sản phẩm có vấn đề',
        },
      ].map((feature, index) => (
        <Col key={index} md={3} className="mb-4 mb-md-0 feature-card">
          <img src={feature.img} alt={feature.title} className="feature-img" />
          <h5 className="mt-3 feature-title">{feature.title}</h5>
          <p className="feature-desc">{feature.desc}</p>
        </Col>
      ))}
    </Row>
  </Container>
);

const HomeScreen = ({ match }) => {
  const keyword = match.params.keyword;
  const pageNumber = match.params.pageNumber || 1;

  const dispatch = useDispatch();
  const productList = useSelector((state) => state.productList);
  const { loading, error, products, page, pages } = productList;

  useEffect(() => {
    dispatch(listProducts(keyword, pageNumber));
  }, [dispatch, keyword, pageNumber]);

  return (
    <>
      <Meta />
      {!keyword && <ProductCarousel />}
      <FeatureSection />
      <Container className="py-5">
        <Row className="justify-content-center">
          {['/images/banner/img2-middle-sinrato1.jpg', '/images/banner/img1-middle-sinrato1.jpg'].map((img, index) => (
            <Col key={index} md={6} className="text-center banner-container">
              <img
                src={img}
                alt={`Top Banner ${index + 1}`}
                className="banner-img"
              />
            </Col>
          ))}
        </Row>
      </Container>

      <Container className="py-5">
        <h2 className="section-title">Sản Phẩm Bán Chạy</h2>
        {loading ? (
          <Loader />
        ) : error ? (
          <Message variant="danger">{error}</Message>
        ) : (
          <Row>
            {products.slice(0, 4).map((product) => (
              <Col key={product._id} sm={12} md={6} lg={3} className="product-card">
                <div className="product-container">
                  <img src={product.image} alt={product.name} className="product-img" />
                  <h5 className="product-title">{product.name}</h5>
                  <p className="product-price">{product.price} ₫</p>
                </div>
              </Col>
            ))}
          </Row>
        )}
      </Container>

      <Banner position="middle" />

      <Container className="py-5">
        <h2 className="section-title">Tất Cả Sản Phẩm</h2>
        {loading ? (
          <Loader />
        ) : error ? (
          <Message variant="danger">{error}</Message>
        ) : (
          <>
            <Row>
              {products.map((product) => (
                <Col key={product._id} sm={12} md={6} lg={4} xl={3} className="product-card">
                  <div className="product-container">
                    <img src={product.image} alt={product.name} className="product-img" />
                    <h5 className="product-title">{product.name}</h5>
                    <p className="product-price">{product.price} ₫</p>
                  </div>
                </Col>
              ))}
            </Row>
            <Paginate pages={pages} page={page} keyword={keyword || ''} />
          </>
        )}
      </Container>

      <div className="footer-banner">
        <img
          src="/images/banner/img-bottom-sinrato1.jpg"
          alt="Footer Banner"
          className="footer-banner-img"
        />
      </div>
    </>
  );
};

export default HomeScreen;
