import React from 'react';
import { Route } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { Navbar, Nav, Container, NavDropdown } from 'react-bootstrap';
import SearchBox from './SearchBox';
import { logout } from '../actions/userActions';

const Header = () => {
  const dispatch = useDispatch();

  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;

  const cartItems = useSelector((state) => state.cart.cartItems);

  const logoutHandler = () => {
    dispatch(logout());
  };

  const totalItems = cartItems.reduce((acc, item) => acc + item.qty, 0);
  const totalPrice = cartItems
    .reduce((acc, item) => acc + item.price * item.qty, 0)
    .toLocaleString('vi-VN', { style: 'currency', currency: 'VND' });

  return (
    <header>
      {/* Middle Header */}
      <div
        style={{
          backgroundColor: '#ffffff', // White background
          padding: '8px 0', // Reduced padding for smaller header
          borderBottom: '1px solid #FFD700', // Yellow bottom border
          position: 'sticky',
          top: 0, // Sticks to the top of the viewport
          zIndex: 1000, // Ensure it stays above other content
        }}
      >
        <Container className="d-flex justify-content-between align-items-center">
          {/* Logo */}
          <Link to="/">
            <img
              src="/images/logo/logo.png"
              alt="Logo"
              style={{
                maxHeight: '60px', // Larger logo size
                marginRight: '1rem',
              }}
            />
          </Link>

          {/* SearchBox */}
          <div className="search-box" style={{ flex: 1, maxWidth: '400px' }}>
            <Route render={({ history }) => <SearchBox history={history} />} />
          </div>

          {/* Cart */}
          <div className="d-flex align-items-center">
            <Link
              to="/wishlist"
              className="cart-link"
              style={{ marginRight: '15px', color: '#FFD700', fontSize: '13px' }}
            >
              <i className="fas fa-heart"></i> Yêu thích
            </Link>
            <Link
              to="/cart"
              className="cart-link"
              style={{ color: '#FFD700', fontSize: '13px' }}
            >
              <i className="fas fa-shopping-cart"></i> {totalPrice} ({totalItems})
            </Link>
          </div>
        </Container>
      </div>

      {/* Navbar */}
      <Navbar
        style={{
          backgroundColor: '#FFD700', 
          padding: '5px 10px', // Smaller padding for compact navbar
        }}
        expand="lg"
        variant="light"
      >
        <Container>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="mx-auto" style={{ fontSize: '13px', fontWeight: '500' }}>
              <Link to="/" className="nav-link">
                Trang chủ
              </Link>
              <Link to="/storescreen" className="nav-link">
                Cửa hàng
              </Link>
              <Link to="/contact" className="nav-link">
                Liên hệ
              </Link>
              <Link to="/about" className="nav-link">
                Về chúng tôi
              </Link>
            </Nav>

            {/* User Account */}
            <Nav>
              {userInfo ? (
                <NavDropdown
                  title={userInfo.name || 'Tài khoản'}
                  id="username"
                  className="nav-link"
                  style={{ fontSize: '13px' }}
                >
                  <Link to="/profile" className="dropdown-item">
                    Thông tin của tôi
                  </Link>
                  <NavDropdown.Item onClick={logoutHandler}>
                    Đăng xuất
                  </NavDropdown.Item>
                  {userInfo.isAdmin && (
                    <Link to="/admin/dashboard" className="dropdown-item">
                      Quản trị
                    </Link>
                  )}
                </NavDropdown>
              ) : (
                <Link to="/login" className="nav-link" style={{ fontSize: '13px' }}>
                  Đăng nhập
                </Link>
              )}
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>
    </header>
  );
};

export default Header;
