import React, { useState } from 'react';
import { Form, Button } from 'react-bootstrap';

const SearchBox = ({ history }) => {
  const [keyword, setKeyword] = useState('');

  const submitHandler = (e) => {
    e.preventDefault();
    if (keyword.trim()) {
      history.push(`/search/${keyword}`);
    } else {
      history.push('/');
    }
  };

  const inputStyle = {
    width: '100%',  // Maximized the width of the input field
    border: '1px solid #ddd',
    padding: '15px', // Increased padding for better appearance
    borderRadius: '5px 0 0 5px',
    outline: 'none',
    fontSize: '16px', // Larger font size
  };

  const buttonStyle = {
    backgroundColor: '#FFD700', // Updated to a yellow color
    border: 'none',
    color: '#111',
    fontWeight: 'bold',
    padding: '15px 20px', // Adjusted padding for a larger button
    borderRadius: '0 5px 5px 0',
    cursor: 'pointer',
    fontSize: '16px', // Larger font size to match input
  };

  const formStyle = {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    maxWidth: '600px', // Constrain the maximum width of the form
    margin: '0 auto', // Center align the form
  };

  return (
    <Form onSubmit={submitHandler} style={formStyle}>
      <input
        type="text"
        name="q"
        onChange={(e) => setKeyword(e.target.value)}
        placeholder="Tìm kiếm mọi thứ ở đây.."
        style={inputStyle}
      />
      <Button type="submit" style={buttonStyle}>
        Tìm kiếm
      </Button>
    </Form>
  );
};

export default SearchBox;
