import React from 'react';
import { Route } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { Navbar, Nav, Container, NavDropdown } from 'react-bootstrap';
import SearchBox from './SearchBox';
import { logout } from '../actions/userActions';

const Header = () => {
  const dispatch = useDispatch();

  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;

  const cartItems = useSelector((state) => state.cart.cartItems);

  const logoutHandler = () => {
    dispatch(logout());
  };

  const totalItems = cartItems.reduce((acc, item) => acc + item.qty, 0);
  const totalPrice = cartItems
    .reduce((acc, item) => acc + item.price * item.qty, 0)
    .toLocaleString('vi-VN', { style: 'currency', currency: 'VND' });

  return (
    <header>
      {/* Top Bar */}
      <div
        style={{
          backgroundColor: '#FFD700', // Màu vàng cho thanh top
          padding: '10px 0',
          textAlign: 'center',
          fontWeight: 'bold',
        }}
      >
        <Container>
          Hotline: 0123456789
        </Container>
      </div>

      {/* Middle Header */}
      <div
        style={{
          backgroundColor: '#ffffff', // Màu trắng cho header chính
          padding: '15px 0',
          borderBottom: '1px solid #FFD700', // Đường viền dưới màu vàng
        }}
      >
        <Container className="d-flex justify-content-between align-items-center">
          {/* Logo */}
          <Link to="/">
            <img
              src="/images/logo/logo.png"
              alt="Logo"
              style={{
                maxHeight: '50px',
                marginRight: '1rem',
              }}
            />
          </Link>

          {/* SearchBox */}
          <div className="search-box" style={{ flex: 1, maxWidth: '500px' }}>
            <Route render={({ history }) => <SearchBox history={history} />} />
          </div>

          {/* Cart */}
          <div className="d-flex align-items-center">
            <Link
              to="/wishlist"
              className="cart-link"
              style={{ marginRight: '20px', color: '#FFD700' }}
            >
              <i className="fas fa-heart"></i> Yêu thích
            </Link>
            <Link
              to="/cart"
              className="cart-link"
              style={{ color: '#FFD700' }}
            >
              <i className="fas fa-shopping-cart"></i> {totalPrice} ({totalItems})
            </Link>
          </div>
        </Container>
      </div>

      {/* Navbar */}
      <Navbar
        style={{ backgroundColor: '#FFD700' }} // Thanh menu màu vàng
        expand="lg"
        variant="light"
      >
        <Container>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="mx-auto">
              <Link to="/" className="nav-link">
                Trang chủ
              </Link>
              <Link to="/storescreen" className="nav-link">
                Cửa hàng
              </Link>
              <Link to="/contact" className="nav-link">
                Liên hệ
              </Link>
              <Link to="/about" className="nav-link">
                Về chúng tôi
              </Link>
            </Nav>

            {/* Tài khoản và Admin */}
            <Nav>
              {userInfo ? (
                <NavDropdown
                  title="Tài khoản"
                  id="username"
                  className="nav-link"
                >
                  <Link to="/profile" className="dropdown-item">
                    Thông tin của tôi
                  </Link>
                  <NavDropdown.Item onClick={logoutHandler}>
                    Đăng xuất
                  </NavDropdown.Item>
                </NavDropdown>
              ) : (
                <Link to="/login" className="nav-link">
                  Đăng nhập
                </Link>
              )}
              {userInfo && userInfo.isAdmin && (
                <Link
                  to="/admin/dashboard"
                  className="nav-link"
                  style={{ color: '#111', fontWeight: 'bold' }}
                >
                  Admin
                </Link>
              )}
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>
    </header>
  );
};

export default Header;
