import React from 'react';
import { Route } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { Navbar, Nav, Container, NavDropdown } from 'react-bootstrap';
import SearchBox from './SearchBox';
import { logout } from '../actions/userActions';

const Header = () => {
  const dispatch = useDispatch();

  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;

  const cartItems = useSelector((state) => state.cart.cartItems);

  const logoutHandler = () => {
    dispatch(logout());
  };

  const totalItems = cartItems.reduce((acc, item) => acc + item.qty, 0);
  const totalPrice = cartItems
    .reduce((acc, item) => acc + item.price * item.qty, 0)
    .toLocaleString('vi-VN', { style: 'currency', currency: 'VND' });

  return (
    <header>
      {/* Middle Header */}
      <div
        style={{
          backgroundColor: '#ffffff', // White background
          padding: '10px 0', // Adjusted padding
          borderBottom: '1px solid #FFD700', // Yellow bottom border
          position: 'sticky',
          top: 0, // Sticks to the top of the viewport
          zIndex: 1000, // Ensure it stays above other content
        }}
      >
        <Container className="d-flex justify-content-between align-items-center">
          {/* Logo */}
          <Link to="/">
            <img
              src="/images/logo/logo.png"
              alt="Logo"
              style={{
                maxHeight: '60px', // Logo size
                marginRight: '1rem',
              }}
            />
          </Link>

          {/* SearchBox */}
          <div className="search-box" style={{ flex: 1, maxWidth: '6000px' }}>
            <Route render={({ history }) => <SearchBox history={history} />} />
          </div>

          {/* Cart */}
          <div className="d-flex align-items-center">
            <Link
              to="/wishlist"
              className="cart-link"
              style={{ marginRight: '20px', color: '#FFD700', fontSize: '14px' }}
            >
              <i className="fas fa-heart"></i> Yêu thích
            </Link>
            <Link
              to="/cart"
              className="cart-link"
              style={{ color: '#FFD700', fontSize: '14px' }}
            >
              <i className="fas fa-shopping-cart"></i> {totalPrice} ({totalItems})
            </Link>
          </div>
        </Container>
      </div>

      {/* Navbar */}
      <Navbar
        style={{
          backgroundColor: '#FFD700',
          padding: '5px 0', // Adjusted navbar padding
        }}
        expand="lg"
        variant="light"
      >
        <Container>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav
              className="mx-auto"
              style={{
                fontSize: '16px', // Adjusted font size for navbar links
                fontWeight: '600', // Make it bold
                textTransform: 'uppercase', // Capitalize text
                letterSpacing: '0.5px', // Adjust spacing
              }}
            >
              <Link to="/" className="nav-link" style={{ padding: '10px 15px' }}>
                Trang chủ
              </Link>
              <Link to="/store" className="nav-link" style={{ padding: '10px 15px' }}>
                Cửa hàng
              </Link>
              <Link to="/contact" className="nav-link" style={{ padding: '10px 15px' }}>
                Liên hệ
              </Link>
              <Link to="/about" className="nav-link" style={{ padding: '10px 15px' }}>
                Về chúng tôi
              </Link>
              {userInfo && userInfo.isAdmin && (
                <NavDropdown title="Admin" id="adminmenu" className="nav-link" style={{ padding: '0 15px' }}>
                  <Link to="/admin/userlist" className="dropdown-item">
                    Người Dùng
                  </Link>
                  <Link to="/admin/productlist" className="dropdown-item">
                    Các Sản Phẩm
                  </Link>
                  <Link to="/admin/orderlist" className="dropdown-item">
                    Đơn Hàng
                  </Link>
                </NavDropdown>
              )}
            </Nav>
          </Navbar.Collapse>

          {/* Account */}
          {userInfo ? (
            <NavDropdown title={userInfo.name} id="username" style={{ color: '#111' }}>
              <Link to="/profile" className="dropdown-item">
                Thông Tin Của Tôi
              </Link>
              <NavDropdown.Item onClick={logoutHandler}>Đăng Xuất</NavDropdown.Item>
            </NavDropdown>
          ) : (
            <Link to="/login" className="nav-link" style={{ color: '#FFD700', marginRight: '10px' }}>
              Đăng Nhập
            </Link>
          )}
        </Container>
      </Navbar>
    </header>
  );
};

export default Header;
