import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Analytics = () => {
  const [summary, setSummary] = useState({
    totalUsers: 0,
    totalProducts: 0,
    totalOrders: 0,
    totalRevenue: 0,
  });

  useEffect(() => {
    const fetchSummary = async () => {
      try {
        const { data } = await axios.get('/api/admin/summary', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
        });
        setSummary(data);
      } catch (error) {
        console.error('Error fetching summary data:', error);
      }
    };

    fetchSummary();
  }, []);

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif', backgroundColor: '#f4f4f9' }}>
      <h1 style={{ textAlign: 'center', marginBottom: '30px' }}>Thống Kê Admin</h1>

      {/* Tổng hợp số liệu */}
      <div style={{ display: 'flex', justifyContent: 'space-around', marginBottom: '40px' }}>
        <div style={cardStyle}>
          <h2>{summary.totalUsers}</h2>
          <p>Người Dùng</p>
        </div>
        <div style={cardStyle}>
          <h2>{summary.totalProducts}</h2>
          <p>Sản Phẩm</p>
        </div>
        <div style={cardStyle}>
          <h2>{summary.totalOrders}</h2>
          <p>Đơn Hàng</p>
        </div>
        <div style={cardStyle}>
          <h2>{summary.totalRevenue.toLocaleString('vi-VN')} VND</h2>
          <p>Doanh Thu</p>
        </div>
      </div>

      {/* Sơ đồ */}
      <div style={{ margin: '40px auto', textAlign: 'center' }}>
        <canvas
          id="revenueChart"
          width="600"
          height="400"
          style={{
            display: 'block',
            margin: '20px auto',
            border: '1px solid #ccc',
            borderRadius: '10px',
          }}
        ></canvas>
      </div>
    </div>
  );
};

// Card style cho các thông tin số liệu
const cardStyle = {
  padding: '20px',
  backgroundColor: '#fff',
  borderRadius: '8px',
  boxShadow: '0px 4px 6px rgba(0, 0, 0, 0.1)',
  textAlign: 'center',
  width: '200px',
};

// Fetch và hiển thị biểu đồ (nếu cần dùng thư viện Chart.js)
useEffect(() => {
  const loadChart = async () => {
    const Chart = (await import('chart.js/auto')).default;

    const ctx = document.getElementById('revenueChart').getContext('2d');
    new Chart(ctx, {
      type: 'bar',
      data: {
        labels: ['Người Dùng', 'Sản Phẩm', 'Đơn Hàng', 'Doanh Thu'],
        datasets: [
          {
            label: 'Thống Kê',
            data: [
              summary.totalUsers,
              summary.totalProducts,
              summary.totalOrders,
              summary.totalRevenue / 1000000, // Convert to million VND
            ],
            backgroundColor: ['#4caf50', '#2196f3', '#ff9800', '#f44336'],
          },
        ],
      },
      options: {
        responsive: true,
        scales: {
          y: {
            beginAtZero: true,
            ticks: {
              callback: function (value) {
                return value.toLocaleString('vi-VN') + 'M';
              },
            },
          },
        },
      },
    });
  };

  loadChart();
}, [summary]);

export default Analytics;
