import React from 'react';
import { Link } from 'react-router-dom';
import { Card, Button } from 'react-bootstrap';
import { FaShoppingCart } from 'react-icons/fa';
import Rating from './Rating';

const Product = ({ product }) => {
  return (
    <Card className="my-3 p-3 rounded product-card shadow-sm">
      {/* Product Image */}
      <div className="image-container">
        <Link to={`/product/${product._id}`} className="product-link">
          <Card.Img src={product.image} variant="top" className="product-image" />
        </Link>
      </div>

      {/* Product Content */}
      <Card.Body>
        {/* Product Name */}
        <Link to={`/product/${product._id}`} className="product-link">
          <Card.Title as="div" className="product-title">
            <strong>{product.name}</strong>
          </Card.Title>
        </Link>

        {/* Product Brand */}
        <Card.Text as="div" className="product-brand text-muted">
          Thương hiệu: <strong>{product.brand}</strong>
        </Card.Text>

        {/* Product Stock */}
        {product.countInStock > 0 ? (
          <Card.Text as="div" className="product-stock text-success">
            Còn lại: {product.countInStock} sản phẩm
          </Card.Text>
        ) : (
          <Card.Text as="div" className="product-stock text-danger">
            Hết hàng
          </Card.Text>
        )}

        {/* Product Rating */}
        <Card.Text as="div" className="product-rating">
          <Rating value={product.rating} text={`${product.numReviews} đánh giá`} />
        </Card.Text>

        {/* Product Price */}
        <Card.Text as="h3" className="product-price">
          {new Intl.NumberFormat('vi-VN').format(product.price)} VNĐ
        </Card.Text>

        {/* Add to Cart Button */}
        {product.countInStock > 0 && (
          <Button variant="primary" className="add-to-cart-btn">
            <FaShoppingCart /> Thêm vào giỏ hàng
          </Button>
        )}
      </Card.Body>
    </Card>
  );
};

export default Product;
