import React from 'react';
import { Image, Carousel } from 'react-bootstrap';

const Banner = () => {
  const banners = [
    '/images/banner/category-image.jpg', // Banner 1
    '/images/banner/sale-img1-top-sinrato1.jpg',    // Banner 2
    '/images/banner/img2-top-sinrato1.jpg',    // Banner 3
    '/images/banner/holiday-deals.jpg',  // Banner 4
  ];

  return (
    <Carousel interval={3000} pause="hover" className="banner-carousel">
      {banners.map((banner, index) => (
        <Carousel.Item key={index}>
          <Image src={banner} alt={`Banner ${index + 1}`} fluid />
        </Carousel.Item>
      ))}
    </Carousel>
  );
};

export default Banner;
