import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { Row, Col, Image, Card, Button, Form, ListGroup } from 'react-bootstrap';
import Rating from '../components/Rating';
import Message from '../components/Message';
import Loader from '../components/Loader';
import Meta from '../components/Meta';
import { listProductDetails, createProductReview } from '../actions/productActions';
import { PRODUCT_CREATE_REVIEW_RESET } from '../constants/productConstants';

const ProductScreen = ({ history, match }) => {
  const [qty, setQty] = useState(1);
  const [rating, setRating] = useState(0);
  const [comment, setComment] = useState('');

  const dispatch = useDispatch();

  const productDetails = useSelector((state) => state.productDetails);
  const { loading, error, product } = productDetails;

  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;

  const productReviewCreate = useSelector((state) => state.productReviewCreate);
  const {
    success: successProductReview,
    loading: loadingProductReview,
    error: errorProductReview,
  } = productReviewCreate;

  useEffect(() => {
    if (successProductReview) {
      setRating(0);
      setComment('');
      dispatch(listProductDetails(match.params.id));
      dispatch({ type: PRODUCT_CREATE_REVIEW_RESET });
    }
    if (!product._id || product._id !== match.params.id) {
      dispatch(listProductDetails(match.params.id));
    }
  }, [dispatch, match, successProductReview, product._id]);

  const addToCartHandler = () => {
    history.push(`/cart/${match.params.id}?qty=${qty}`);
  };

  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(createProductReview(match.params.id, { rating, comment }));
  };

  return (
    <>
      <Link className="btn btn-secondary my-3" to="/">
        Quay Lại
      </Link>
      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant="danger">{error}</Message>
      ) : (
        <>
          <Meta title={product.name} />
          <Row>
            <Col md={6}>
              <Card className="shadow-lg p-3 mb-5 bg-body rounded">
                <Image src={product.image} alt={product.name} fluid />
              </Card>
            </Col>
            <Col md={6}>
              <Card className="shadow p-3 mb-5 bg-body rounded">
                <ListGroup variant="flush">
                  <ListGroup.Item>
                    <h3 className="text-uppercase text-primary">{product.name}</h3>
                  </ListGroup.Item>
                  <ListGroup.Item>
                    <Rating
                      value={product.rating}
                      text={`${product.numReviews} đánh giá`}
                      color="#f8e825"
                    />
                  </ListGroup.Item>
                  <ListGroup.Item>
                    <strong>Giá: </strong>{product.price.toLocaleString()} VNĐ
                  </ListGroup.Item>
                  <ListGroup.Item>
                    <p>{product.description}</p>
                  </ListGroup.Item>
                </ListGroup>
                <ListGroup.Item>
                  <Row>
                    <Col>Trạng Thái:</Col>
                    <Col>
                      {product.countInStock > 0 ? (
                        <span className="text-success">Còn Hàng</span>
                      ) : (
                        <span className="text-danger">Hết Hàng</span>
                      )}
                    </Col>
                  </Row>
                </ListGroup.Item>
                {product.countInStock > 0 && (
                  <ListGroup.Item>
                    <Row>
                      <Col>Số Lượng:</Col>
                      <Col>
                        <Form.Control
                          as="select"
                          value={qty}
                          onChange={(e) => setQty(e.target.value)}
                        >
                          {[...Array(product.countInStock).keys()].map((x) => (
                            <option key={x + 1} value={x + 1}>
                              {x + 1}
                            </option>
                          ))}
                        </Form.Control>
                      </Col>
                    </Row>
                  </ListGroup.Item>
                )}
                <ListGroup.Item>
                  <Button
                    onClick={addToCartHandler}
                    className="btn-block btn-primary"
                    type="button"
                    disabled={product.countInStock === 0}
                  >
                    {product.countInStock > 0 ? 'Thêm Vào Giỏ Hàng' : 'Hết Hàng'}
                  </Button>
                </ListGroup.Item>
              </Card>
            </Col>
          </Row>
        </>
      )}
    </>
  );
};

export default ProductScreen;
