import React, { useState, useEffect } from 'react';
import { Row, Col, Image, Button, Form } from 'react-bootstrap';
import { useDispatch, useSelector } from 'react-redux';
import Product from '../components/Product';
import Loader from '../components/Loader';
import Message from '../components/Message';
import { listProducts } from '../actions/productActions';

const StoreScreen = () => {
  const [priceRange, setPriceRange] = useState(10000000); // Giá mặc định là 10,000,000
  const [sortOption, setSortOption] = useState(''); // Lựa chọn sắp xếp
  const [selectedBrands, setSelectedBrands] = useState([]); // Các thương hiệu được chọn

  const dispatch = useDispatch();

  const productList = useSelector((state) => state.productList);
  const { loading, error, products } = productList;

  useEffect(() => {
    dispatch(listProducts());
  }, [dispatch]);

  // Danh sách thương hiệu (giả lập)
  const brands = ['Apple', 'Samsung', 'Sony', 'LG', 'Dell'];

  // Lọc sản phẩm theo giá và thương hiệu
  const filteredProducts = products
    .filter((product) => product.price <= priceRange)
    .filter((product) =>
      selectedBrands.length > 0 ? selectedBrands.includes(product.brand) : true
    )
    .sort((a, b) => {
      if (sortOption === 'name') {
        return a.name.localeCompare(b.name);
      } else if (sortOption === 'price') {
        return a.price - b.price;
      }
      return 0;
    });

  const handleSliderChange = (e) => {
    setPriceRange(parseInt(e.target.value, 10));
  };

  const handleSort = (option) => {
    setSortOption(option);
  };

  const handleBrandToggle = (brand) => {
    setSelectedBrands((prev) =>
      prev.includes(brand) ? prev.filter((b) => b !== brand) : [...prev, brand]
    );
  };

  return (
    <Row>
      {/* Sidebar */}
      <Col md={3} className="mb-4">
        <div
          className="filter-section"
          style={{ padding: '20px', background: '#f9f9f9', borderRadius: '8px' }}
        >
          <h4 style={{ color: '#333' }}>Lọc Theo Giá</h4>
          <div style={{ marginBottom: '20px', textAlign: 'center', color: '#555' }}>
            <span>Đến: {priceRange.toLocaleString()} VND</span>
          </div>
          <input
            type="range"
            min="0"
            max="20000000"
            step="500000"
            value={priceRange}
            onChange={handleSliderChange}
            style={{
              width: '100%',
              accentColor: '#FFD700',
            }}
          />
          <Button variant="primary" className="mt-3" style={{ width: '100%' }}>
            Lọc
          </Button>
        </div>

        {/* Filter by Brand */}
        <div className="filter-section mt-4">
          <h4 style={{ color: '#333', marginBottom: '15px' }}>Tìm Theo Thương Hiệu</h4>
          <ul className="brand-filter">
            {['Apple', 'Cannon', 'Sony', 'Logitech', 'Amazon', 'Vivo', 'Oppo', 'Realme', 'Sony'].map(
              (brand, index) => (
                <li key={index}>
                  <input
                    type="radio"
                    name="brand" // Nhóm name để chỉ cho phép chọn 1 nút
                    value={brand}
                    onChange={(e) => console.log('Thương hiệu:', e.target.value)} // Thay đổi logic xử lý
                  />
                  <label>{brand}</label>
                </li>
              )
            )}
          </ul>
        </div>


        {/* Banner dưới thanh trượt */}
        <div style={{ marginTop: '20px', textAlign: 'center' }}>
          <Image
            src="/images/banner/img-module2.jpg"
            alt="Banner Phụ"
            fluid
            style={{ borderRadius: '8px' }}
          />
        </div>
        <div style={{ marginTop: '20px', textAlign: 'center' }}>
          <Image
            src="/images/banner/img-module3.jpg"
            alt="Banner Phụ"
            fluid
            style={{ borderRadius: '8px' }}
          />
        </div>
      </Col>

      {/* Main Content */}
      <Col md={9}>
        {/* Banner */}
        <div style={{ marginBottom: '20px', position: 'relative' }}>
          <Image
            src="/images/banner/category-image.jpg" // Đường dẫn đến ảnh banner
            alt="Banner Cửa Hàng"
            fluid
            style={{ width: '100%', borderRadius: '8px' }}
          />
          <h1
            style={{
              position: 'absolute',
              top: '50%',
              left: '50%',
              transform: 'translate(-50%, -50%)',
              color: '#fff',
              textShadow: '2px 2px 8px rgba(0,0,0,0.7)',
              fontSize: '2rem',
            }}
          >
            Cửa Hàng
          </h1>
        </div>

        {/* Sort Buttons */}
        <div className="sort-buttons mb-4" style={{ textAlign: 'right' }}>
          <Button
            variant={sortOption === 'name' ? 'primary' : 'outline-primary'}
            className="me-2"
            onClick={() => handleSort('name')}
          >
            Tên
          </Button>
          <Button
            variant={sortOption === 'price' ? 'primary' : 'outline-primary'}
            onClick={() => handleSort('price')}
          >
            Giá ↑
          </Button>
        </div>

        {/* Product List */}
        {loading ? (
          <Loader />
        ) : error ? (
          <Message variant="danger">{error}</Message>
        ) : (
          <Row>
            {filteredProducts.map((product) => (
              <Col key={product._id} sm={12} md={6} lg={4} xl={3} className="mb-4">
                <Product product={product} />
              </Col>
            ))}
          </Row>
        )}
      </Col>
    </Row>
  );
};

export default StoreScreen;
