import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Product from '../components/Product';
import Loader from '../components/Loader';
import Message from '../components/Message';
import { listProducts } from '../actions/productActions';


const StoreScreen = () => {
  const [priceRange, setPriceRange] = useState(10000000);
  const [sortOption, setSortOption] = useState('');
  const [selectedBrands, setSelectedBrands] = useState([]);

  const dispatch = useDispatch();

  const productList = useSelector((state) => state.productList);
  const { loading, error, products } = productList;

  useEffect(() => {
    dispatch(listProducts());
  }, [dispatch]);

  const brands = ['Apple', 'Samsung', 'Sony', 'LG', 'Dell'];

  const filteredProducts = products
    .filter((product) => product.price <= priceRange)
    .filter((product) =>
      selectedBrands.length > 0 ? selectedBrands.includes(product.brand) : true
    )
    .sort((a, b) => {
      if (sortOption === 'name') {
        return a.name.localeCompare(b.name);
      } else if (sortOption === 'price') {
        return a.price - b.price;
      }
      return 0;
    });

  const handleSliderChange = (e) => {
    setPriceRange(parseInt(e.target.value, 10));
  };

  const handleSort = (option) => {
    setSortOption(option);
  };

  const handleBrandToggle = (brand) => {
    setSelectedBrands((prev) =>
      prev.includes(brand) ? prev.filter((b) => b !== brand) : [...prev, brand]
    );
  };

  return (
    <div className="store-container">
      {/* Sidebar */}
      <div className="store-sidebar">
        <div className="filter-section">
          <h4>Lọc Theo Giá</h4>
          <div className="price-range">Đến: {priceRange.toLocaleString()} VND</div>
          <input
            type="range"
            min="0"
            max="20000000"
            step="500000"
            value={priceRange}
            onChange={handleSliderChange}
          />
        </div>
        <div className="filter-section">
          <h4>Lọc Theo Thương Hiệu</h4>
          {brands.map((brand) => (
            <label key={brand} className="brand-filter">
              <input
                type="checkbox"
                checked={selectedBrands.includes(brand)}
                onChange={() => handleBrandToggle(brand)}
              />
              {brand}
            </label>
          ))}
        </div>
      </div>

      {/* Main Content */}
      <div className="store-main">
        {/* Banner */}
        <div className="store-banner">
          <img
            src="/images/banner/category-image.jpg"
            alt="Banner Cửa Hàng"
          />
          <h1>Cửa Hàng</h1>
        </div>

        {/* Sort Buttons */}
        <div className="sort-buttons">
          <button
            className={sortOption === 'name' ? 'active' : ''}
            onClick={() => handleSort('name')}
          >
            Tên
          </button>
          <button
            className={sortOption === 'price' ? 'active' : ''}
            onClick={() => handleSort('price')}
          >
            Giá ↑
          </button>
        </div>

        {/* Product List */}
        {loading ? (
          <Loader />
        ) : error ? (
          <Message variant="danger">{error}</Message>
        ) : (
          <div className="product-grid">
            {filteredProducts.map((product) => (
              <Product key={product._id} product={product} />
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default StoreScreen;
