import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Analytics = () => {
  const [totalSales, setTotalSales] = useState(0);
  const [salesByBrand, setSalesByBrand] = useState([]);

  useEffect(() => {
    const fetchSalesStats = async () => {
      try {
        const { data } = await axios.get('/api/stats/sales'); // Gọi API
        setTotalSales(data.totalSales);
        setSalesByBrand(data.salesByBrand);
      } catch (error) {
        console.error('Error fetching sales stats:', error);
      }
    };

    fetchSalesStats();
  }, []);

  return (
    <div className="analytics-container">
      <h1 className="analytics-title">Thống Kê Bán Hàng</h1>

      {/* Tổng sản phẩm bán được */}
      <div className="summary-section">
        <h2>Tổng Sản Phẩm Đã Bán:</h2>
        <p className="total-sales">{totalSales} sản phẩm</p>
      </div>

      {/* Thống kê theo hãng */}
      <div className="brand-stats-section">
        <h2>Thống Kê Theo Hãng</h2>
        <table className="brand-stats-table">
          <thead>
            <tr>
              <th>Hãng</th>
              <th>Số Lượng Bán</th>
            </tr>
          </thead>
          <tbody>
            {salesByBrand.map((item, index) => (
              <tr key={index}>
                <td>{item.brand}</td>
                <td>{item.sold}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Analytics;
