import React from 'react';
import { Container, Row, Col } from 'react-bootstrap';

const footerStyle = {
	backgroundColor: '#f9f9f9',
	padding: '60px 20px', // Tăng padding trên và dưới
	color: '#333',
	fontSize: '14px',
	marginTop: '40px', // Tạo khoảng cách giữa footer và banner
};

const sectionTitleStyle = {
	fontWeight: 'bold',
	marginBottom: '15px', // Tăng khoảng cách giữa tiêu đề và nội dung
	fontSize: '18px', // Tăng kích thước chữ tiêu đề
	textAlign: 'left',
};

const linkStyle = {
	color: '#333',
	textDecoration: 'none',
	fontSize: '14px',
	marginBottom: '12px', // Tăng khoảng cách giữa các link
	display: 'block',
};

const inputStyle = {
	width: 'calc(100% - 130px)', // Đặt kích thước input rộng hơn
	padding: '12px',
	marginRight: '10px',
	borderRadius: '4px',
	border: '1px solid #ced4da',
	boxSizing: 'border-box',
	display: 'inline-block',
};

const buttonStyle = {
	padding: '12px 25px', // Tăng kích thước nút
	backgroundColor: '#ffc107',
	color: '#333',
	border: 'none',
	borderRadius: '4px',
	cursor: 'pointer',
	fontSize: '14px',
	display: 'inline-block',
	verticalAlign: 'top',
};

const socialIconsStyle = {
	display: 'flex',
	gap: '15px', // Tăng khoảng cách giữa các icon mạng xã hội
	justifyContent: 'center',
	marginTop: '30px', // Tăng khoảng cách giữa icon và nội dung phía trên
};

const iconStyle = {
	fontSize: '22px', // Tăng kích thước icon
	color: '#333',
	transition: 'color 0.3s',
};

const copyrightStyle = {
	marginTop: '30px', // Tăng khoảng cách giữa bản quyền và các icon
	fontSize: '13px', // Làm nhỏ hơn chút
	color: '#6c757d',
	textAlign: 'center',
};


const Footer = () => {
	const handleSubmit = (e) => {
		e.preventDefault();
		alert('Cảm ơn bạn đã đăng ký!');
	};

	return (
		<footer style={footerStyle}>
			<Container>
				{/* Phần đăng ký email */}
				<Row style={{ backgroundColor: '#f1f1f1', padding: '20px', borderRadius: '5px', marginBottom: '20px', justifyContent: 'center' }}>
					<Col md={8} style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
						<h3 style={{ margin: 0 }}>Đăng ký để nhận tin mới</h3>
						<form onSubmit={handleSubmit} style={{ display: 'flex', alignItems: 'center' }}>
							<input type="email" placeholder="Email của bạn" style={inputStyle} aria-label="Email của bạn" required />
							<button style={buttonStyle} type="submit">Gửi !</button>
						</form>
					</Col>
				</Row>

				{/* Các mục chính */}
				<Row style={{ marginBottom: '20px' }}>
					<Col md={4}>
						<h4 style={sectionTitleStyle}>Về NghienDT</h4>
						<a href="#" style={linkStyle}>Giới Thiệu</a>
						<a href="#" style={linkStyle}>Điều Khoản Sử Dụng</a>
						<a href="#" style={linkStyle}>Chính Sách Bảo Mật</a>
						<a href="#" style={linkStyle}>Quy Định Cần Biết</a>
						<a href="#" style={linkStyle}>Liên Hệ Hỗ Trợ</a>
					</Col>
					<Col md={4}>
						<h4 style={sectionTitleStyle}>Hỗ Trợ Khách Hàng</h4>
						<a href="#" style={linkStyle}>Cách Thức Thanh Toán</a>
						<a href="#" style={linkStyle}>Trả Hàng & Hoàn Tiền</a>
						<a href="#" style={linkStyle}>Chính Sách Mua Hàng</a>
						<a href="#" style={linkStyle}>Chính Sách Bảo Hành</a>
						<a href="#" style={linkStyle}>Vận Chuyển Hàng</a>
					</Col>
					<Col md={4}>
						<h4 style={sectionTitleStyle}>Liên Hệ</h4>
						<p style={linkStyle}><span>Địa chỉ:</span> Viện Khoa học Kĩ thuật Bưu điện</p>
						<p style={linkStyle}><span>Email:</span> abc@gmail.com</p>
						<p style={linkStyle}><span>Điện thoại:</span> 0123456789</p>
					</Col>
				</Row>

				{/* Mạng xã hội */}
				<Row>
					<Col style={{ textAlign: 'center' }}>
						<div style={socialIconsStyle}>
							<a href="https://www.facebook.com" style={iconStyle} aria-label="Facebook"><i className="fab fa-facebook"></i></a>
							<a href="https://plus.google.com/discover" style={iconStyle} aria-label="Google Plus"><i className="fab fa-google-plus"></i></a>
							<a href="https://twitter.com" style={iconStyle} aria-label="Twitter"><i className="fab fa-twitter"></i></a>
							<a href="https://www.youtube.com" style={iconStyle} aria-label="YouTube"><i className="fab fa-youtube"></i></a>
						</div>
					</Col>
				</Row>

				{/* Bản quyền */}
				<Row>
					<Col style={{ textAlign: 'center' }}>
						<p style={copyrightStyle}>© {new Date().getFullYear()} Bản quyền thuộc về Phạm Khánh</p>
					</Col>
				</Row>
			</Container>
		</footer>
	);
};

export default Footer;
