import React, { useEffect } from 'react';

const AdminAnalytics = () => {
  const data = {
    sales: [200, 400, 300, 700, 500, 800, 600], // Weekly sales
    orders: [10, 20, 15, 30, 25, 35, 40], // Weekly orders
    labels: ['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'], // Days of the week
    inventory: [
      { name: 'Product A', currentStock: 50, sold: 150 },
      { name: 'Product B', currentStock: 30, sold: 70 },
      { name: 'Product C', currentStock: 80, sold: 120 },
    ],
    purchaseHistory: [
      { customer: 'Alice', items: 3, total: '$50', date: '2024-12-18' },
      { customer: 'Bob', items: 5, total: '$120', date: '2024-12-19' },
      { customer: 'Charlie', items: 2, total: '$30', date: '2024-12-20' },
    ],
  };

  useEffect(() => {
    drawBarChart('salesChart', data.sales, data.labels, '#007bff');
    drawLineChart('ordersChart', data.orders, data.labels, '#28a745');
  }, []);

  const drawBarChart = (id, dataset, labels, color) => {
    const canvas = document.getElementById(id);
    const ctx = canvas.getContext('2d');
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    const maxVal = Math.max(...dataset);
    const barWidth = (canvas.width - 50) / dataset.length;

    dataset.forEach((value, index) => {
      const barHeight = (value / maxVal) * (canvas.height - 50);
      const x = 50 + index * barWidth;
      const y = canvas.height - barHeight - 20;

      // Bar
      ctx.fillStyle = color;
      ctx.fillRect(x, y, barWidth - 10, barHeight);

      // Value
      ctx.fillStyle = '#333';
      ctx.fillText(value, x + barWidth / 2 - 5, y - 5);

      // Label
      ctx.fillText(labels[index], x + barWidth / 2 - 5, canvas.height - 5);
    });
  };

  const drawLineChart = (id, dataset, labels, color) => {
    const canvas = document.getElementById(id);
    const ctx = canvas.getContext('2d');
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    const maxVal = Math.max(...dataset);
    const pointSpacing = (canvas.width - 50) / (dataset.length - 1);

    ctx.beginPath();
    dataset.forEach((value, index) => {
      const x = 50 + index * pointSpacing;
      const y = canvas.height - (value / maxVal) * (canvas.height - 50) - 20;

      if (index === 0) {
        ctx.moveTo(x, y);
      } else {
        ctx.lineTo(x, y);
      }

      // Point
      ctx.fillStyle = color;
      ctx.beginPath();
      ctx.arc(x, y, 5, 0, Math.PI * 2);
      ctx.fill();

      // Value
      ctx.fillStyle = '#333';
      ctx.fillText(value, x, y - 10);

      // Label
      ctx.fillText(labels[index], x, canvas.height - 5);
    });
    ctx.strokeStyle = color;
    ctx.stroke();
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif', backgroundColor: '#f4f4f9' }}>
      <button
        onClick={() => window.history.back()}
        style={{
          padding: '10px 15px',
          marginBottom: '20px',
          border: 'none',
          backgroundColor: '#007bff',
          color: '#fff',
          cursor: 'pointer',
          borderRadius: '5px',
        }}
      >
        Quay Lại
      </button>
      <h1 style={{ textAlign: 'center', color: '#333', marginBottom: '30px' }}>Thống Kê Admin</h1>

      <div style={{ marginBottom: '40px' }}>
        <h2 style={{ color: '#007bff', textAlign: 'center' }}>Doanh Thu Hàng Tuần</h2>
        <canvas id="salesChart" width="600" height="400" style={{ border: '1px solid #ccc', display: 'block', margin: '20px auto' }}></canvas>
      </div>

      <div style={{ marginBottom: '40px' }}>
        <h2 style={{ color: '#28a745', textAlign: 'center' }}>Số Lượng Đơn Hàng Hàng Tuần</h2>
        <canvas id="ordersChart" width="600" height="400" style={{ border: '1px solid #ccc', display: 'block', margin: '20px auto' }}></canvas>
      </div>

      <div style={{ marginBottom: '40px' }}>
        <h2 style={{ color: '#ffc107', textAlign: 'center' }}>Thống Kê Tồn Kho Hiện Tại</h2>
        <table style={{ width: '80%', margin: '20px auto', borderCollapse: 'collapse', border: '1px solid #ccc' }}>
          <thead>
            <tr>
              <th style={{ border: '1px solid #ccc', padding: '10px' }}>Tên Sản Phẩm</th>
              <th style={{ border: '1px solid #ccc', padding: '10px' }}>Tồn Kho Hiện Tại</th>
              <th style={{ border: '1px solid #ccc', padding: '10px' }}>Đã Bán</th>
            </tr>
          </thead>
          <tbody>
            {data.inventory.map((product, index) => (
              <tr key={index}>
                <td style={{ border: '1px solid #ccc', padding: '10px' }}>{product.name}</td>
                <td style={{ border: '1px solid #ccc', padding: '10px' }}>{product.currentStock}</td>
                <td style={{ border: '1px solid #ccc', padding: '10px' }}>{product.sold}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default AdminAnalytics;
