import React, { useEffect, useState } from 'react';
import { Route } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { Navbar, Nav, Container, NavDropdown } from 'react-bootstrap';
import SearchBox from './SearchBox';
import { logout } from '../actions/userActions';

const Header = () => {
  const dispatch = useDispatch();
  const [isScrolled, setIsScrolled] = useState(false);

  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;

  const cartItems = useSelector((state) => state.cart.cartItems);

  const logoutHandler = () => {
    dispatch(logout());
  };

  const totalItems = cartItems.reduce((acc, item) => acc + item.qty, 0);
  const totalPrice = cartItems
    .reduce((acc, item) => acc + item.price * item.qty, 0)
    .toLocaleString('vi-VN', { style: 'currency', currency: 'VND' });

  // Lắng nghe sự kiện cuộn
  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 50) {
        setIsScrolled(true);
      } else {
        setIsScrolled(false);
      }
    };
    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <header>
      {/* Middle Header */}
      <div
        className={`main-header ${isScrolled ? 'scrolled' : ''}`}
        style={{
          backgroundColor: isScrolled ? '#23272b' : '#343a40',
          padding: '10px 0',
          borderBottom: '2px solid #FFC107',
          position: 'sticky',
          top: 0,
          zIndex: 1000,
          transition: 'all 0.3s ease',
          boxShadow: isScrolled ? '0 2px 10px rgba(0, 0, 0, 0.2)' : 'none',
        }}
      >
        <Container className="d-flex justify-content-between align-items-center">
          {/* Logo */}
          <Link to="/">
            <img
              src="/images/logo/logo.png"
              alt="Logo"
              style={{
                maxHeight: '50px',
                marginRight: '1rem',
                transition: 'transform 0.3s ease',
              }}
              className={isScrolled ? 'logo-small' : 'logo-large'}
            />
          </Link>

          {/* SearchBox */}
          <div className="search-box" style={{ flex: 1, maxWidth: '600px' }}>
            <Route render={({ history }) => <SearchBox history={history} />} />
          </div>

          {/* Cart */}
          <div className="d-flex align-items-center">
            <Link
              to="/wishlist"
              className="cart-link"
              style={{
                marginRight: '20px',
                color: '#FFC107',
                fontSize: '14px',
                display: 'flex',
                alignItems: 'center',
                textDecoration: 'none',
              }}
            >
              <i className="fas fa-heart" style={{ marginRight: '5px' }}></i>
              Yêu thích
            </Link>
            <Link
              to="/cart"
              className="cart-link"
              style={{
                color: '#FFC107',
                fontSize: '14px',
                display: 'flex',
                alignItems: 'center',
                textDecoration: 'none',
              }}
            >
              <i className="fas fa-shopping-cart" style={{ marginRight: '5px' }}></i>
              {totalPrice} ({totalItems})
            </Link>
          </div>
        </Container>
      </div>

      {/* Navbar */}
      <Navbar
        style={{
          backgroundColor: '#FFC107',
          padding: '10px 0',
          boxShadow: '0 2px 8px rgba(0, 0, 0, 0.1)',
        }}
        expand="lg"
        variant="light"
      >
        <Container>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav
              className="mx-auto d-flex align-items-center"
              style={{
                fontSize: '16px',
                fontWeight: 'bold',
                textTransform: 'uppercase',
                letterSpacing: '0.5px',
              }}
            >
              {/* Main navigation links */}
              <Link
                to="/"
                className="nav-link"
                style={{
                  padding: '10px 15px',
                  color: '#343a40',
                  transition: 'color 0.3s',
                }}
              >
                Trang chủ
              </Link>
              <Link
                to="/store"
                className="nav-link"
                style={{
                  padding: '10px 15px',
                  color: '#343a40',
                  transition: 'color 0.3s',
                }}
              >
                Cửa hàng
              </Link>
              <Link
                to="/contact"
                className="nav-link"
                style={{
                  padding: '10px 15px',
                  color: '#343a40',
                  transition: 'color 0.3s',
                }}
              >
                Liên hệ
              </Link>
              <Link
                to="/about"
                className="nav-link"
                style={{
                  padding: '10px 15px',
                  color: '#343a40',
                  transition: 'color 0.3s',
                }}
              >
                Về chúng tôi
              </Link>

              {/* Admin Menu */}
              {userInfo && userInfo.isAdmin && (
                <NavDropdown
                  title="Admin"
                  id="adminmenu"
                  style={{
                    padding: '10px 15px',
                    marginLeft: '20px',
                    color: '#343a40',
                  }}
                >
                  <Link to="/admin/userlist" className="dropdown-item">
                    Người Dùng
                  </Link>
                  <Link to="/admin/productlist" className="dropdown-item">
                    Các Sản Phẩm
                  </Link>
                  <Link to="/admin/orderlist" className="dropdown-item">
                    Đơn Hàng
                  </Link>
                </NavDropdown>
              )}

              {/* User Account */}
              {userInfo ? (
                <NavDropdown
                  title={userInfo.name}
                  id="username"
                  style={{
                    padding: '10px 15px',
                    marginLeft: '20px',
                    color: '#343a40',
                  }}
                >
                  <Link to="/profile" className="dropdown-item">
                    Thông Tin Của Tôi
                  </Link>
                  <NavDropdown.Item onClick={logoutHandler}>
                    Đăng Xuất
                  </NavDropdown.Item>
                </NavDropdown>
              ) : (
                <Link
                  to="/login"
                  className="nav-link"
                  style={{
                    color: '#343a40',
                    padding: '10px 15px',
                    marginLeft: '20px',
                  }}
                >
                  Đăng Nhập
                </Link>
              )}
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>
    </header>
  );
};

export default Header;
