const Header = () => {
  const dispatch = useDispatch();

  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;

  const cartItems = useSelector((state) => state.cart.cartItems);

  const logoutHandler = () => {
    dispatch(logout());
  };

  const totalItems = cartItems.reduce((acc, item) => acc + item.qty, 0);
  const totalPrice = cartItems
    .reduce((acc, item) => acc + item.price * item.qty, 0)
    .toLocaleString('vi-VN', { style: 'currency', currency: 'VND' });

  return (
    <header>
      {/* Middle Header */}
      <div
        style={{
          backgroundColor: '#343a40', // Màu nền tối đồng bộ với footer
          padding: '10px 0',
          borderBottom: '1px solid #FFC107', // Đường viền vàng nhấn mạnh
          position: 'sticky',
          top: 0,
          zIndex: 1000,
        }}
      >
        <Container className="d-flex justify-content-between align-items-center">
          {/* Logo */}
          <Link to="/">
            <img
              src="/images/logo/logo.png"
              alt="Logo"
              style={{
                maxHeight: '60px',
                marginRight: '1rem',
              }}
            />
          </Link>

          {/* SearchBox */}
          <div className="search-box" style={{ flex: 1, maxWidth: '600px' }}>
            <Route render={({ history }) => <SearchBox history={history} />} />
          </div>

          {/* Cart */}
          <div className="d-flex align-items-center">
            <Link
              to="/wishlist"
              className="cart-link"
              style={{ marginRight: '20px', color: '#FFC107', fontSize: '14px' }}
            >
              <i className="fas fa-heart"></i> Yêu thích
            </Link>
            <Link
              to="/cart"
              className="cart-link"
              style={{ color: '#FFC107', fontSize: '14px' }}
            >
              <i className="fas fa-shopping-cart"></i> {totalPrice} ({totalItems})
            </Link>
          </div>
        </Container>
      </div>

      {/* Navbar */}
      <Navbar
        style={{
          backgroundColor: '#FFC107', // Màu vàng nhấn mạnh
          padding: '5px 0',
        }}
        expand="lg"
        variant="light"
      >
        <Container>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav
              className="mx-auto d-flex align-items-center"
              style={{
                fontSize: '16px',
                fontWeight: '600',
                textTransform: 'uppercase',
                letterSpacing: '0.5px',
              }}
            >
              {/* Main navigation links */}
              <Link to="/" className="nav-link" style={{ padding: '10px 15px', color: '#343a40' }}>
                Trang chủ
              </Link>
              <Link
                to="/store"
                className="nav-link"
                style={{ padding: '10px 15px', color: '#343a40' }}
              >
                Cửa hàng
              </Link>
              <Link
                to="/contact"
                className="nav-link"
                style={{ padding: '10px 15px', color: '#343a40' }}
              >
                Liên hệ
              </Link>
              <Link
                to="/about"
                className="nav-link"
                style={{ padding: '10px 15px', color: '#343a40' }}
              >
                Về chúng tôi
              </Link>

              {/* Conditional rendering for Admin */}
              {userInfo && userInfo.isAdmin && (
                <NavDropdown
                  title="Admin"
                  id="adminmenu"
                  className="nav-link admin-dropdown"
                  style={{ padding: '10px 15px', marginLeft: '20px', color: '#343a40' }}
                >
                  <Link to="/admin/userlist" className="dropdown-item">
                    Người Dùng
                  </Link>
                  <Link to="/admin/productlist" className="dropdown-item">
                    Các Sản Phẩm
                  </Link>
                  <Link to="/admin/orderlist" className="dropdown-item">
                    Đơn Hàng
                  </Link>
                </NavDropdown>
              )}

              {/* Conditional rendering for User Account */}
              {userInfo ? (
                <NavDropdown
                  title={userInfo.name}
                  id="username"
                  style={{ padding: '10px 15px', marginLeft: '20px', color: '#343a40' }}
                >
                  <Link to="/profile" className="dropdown-item">
                    Thông Tin Của Tôi
                  </Link>
                  <NavDropdown.Item onClick={logoutHandler}>
                    Đăng Xuất
                  </NavDropdown.Item>
                </NavDropdown>
              ) : (
                <Link
                  to="/login"
                  className="nav-link"
                  style={{
                    color: '#343a40',
                    padding: '10px 15px',
                    marginLeft: '20px',
                  }}
                >
                  Đăng Nhập
                </Link>
              )}
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>
    </header>
  );
};

export default Header;
