import React from 'react';
import { Route } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { Navbar, Nav, Container, NavDropdown } from 'react-bootstrap';
import SearchBox from './SearchBox';
import { logout } from '../actions/userActions';

const Header = () => {
  const dispatch = useDispatch();

  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;

  const cartItems = useSelector((state) => state.cart.cartItems);

  const logoutHandler = () => {
    dispatch(logout());
  };

  const totalItems = cartItems.reduce((acc, item) => acc + item.qty, 0);
  const totalPrice = cartItems
    .reduce((acc, item) => acc + item.price * item.qty, 0)
    .toLocaleString('vi-VN', { style: 'currency', currency: 'VND' });

  return (
    <header>
      {/* Top Bar */}
      <div className="top-bar">
        <Container className="d-flex justify-content-between align-items-center">
          <div>
            <span>Telephone Enquiry: </span>
            <a href="tel:+123123321345" className="melvin-kisten">
              (+123) 123 321 345
            </a>
          </div>
          <div>
            {userInfo ? (
              <NavDropdown title={userInfo.name} id="username" className="d-inline">
                <Link to="/profile" className="dropdown-item">
                  Profile
                </Link>
                <NavDropdown.Item onClick={logoutHandler}>Logout</NavDropdown.Item>
              </NavDropdown>
            ) : (
              <Link to="/login" className="melvin-kisten">
                <strong>Sign In</strong>
              </Link>
            )}
          </div>
        </Container>
      </div>

      <div className="header-middle">
        <Container className="d-flex justify-content-between align-items-center">
          {/* Logo */}
          <Link to="/">
            <img
              src="/images/logo/logo.png"
              alt="Logo"
              style={{
                maxHeight: '50px',
                marginRight: '1rem',
              }}
            />
          </Link>

          {/* SearchBox */}
          <div className="search-box" style={{ flex: 1, maxWidth: '500px' }}>
            <Route
              render={({ history }) => <SearchBox history={history} />}
            />
          </div>

          {/* Cart */}
          <div>
            <Link to="/cart" className="cart-link">
              <i className="fas fa-shopping-cart"></i> {totalPrice} ({totalItems})
            </Link>
          </div>
        </Container>
      </div>

      {/* Navbar */}
      <Navbar className="navbar" expand="lg">
        <Container>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="mx-auto">
              <Link to="/storescreen" className="nav-link">
                Shop
              </Link>
              <NavDropdown title="Menu" id="menu">
                <Link to="/shop/all" className="dropdown-item">
                  All
                </Link>
                <NavDropdown title="Male" id="male-menu" className="dropdown-submenu">
                  <Link to="/shop/male/shoes" className="dropdown-item">
                    Shoes
                  </Link>
                  <Link to="/shop/male/shirts" className="dropdown-item">
                    Shirts
                  </Link>
                </NavDropdown>
                <NavDropdown title="Female" id="female-menu" className="dropdown-submenu">
                  <Link to="/shop/female/dresses" className="dropdown-item">
                    Dresses
                  </Link>
                  <Link to="/shop/female/bags" className="dropdown-item">
                    Bags
                  </Link>
                </NavDropdown>
              </NavDropdown>
              <Link to="/event" className="nav-link">
                Event
              </Link>
              <Link to="/contact" className="nav-link">
                Contact
              </Link>
              <Link to="/about" className="nav-link">
                About Us
              </Link>
              {/* Admin Button */}
              {userInfo && userInfo.isAdmin && (
                <Link to="/admin/dashboard" className="nav-link">
                  Admin
                </Link>
              )}
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>
    </header>
  );
};

export default Header;
