import React from 'react'
import { Container, Row, Col } from 'react-bootstrap'

const footerStyle = {
	backgroundColor: '#282c34',
	padding: '40px 0',
	color: '#ffffff',
	textAlign: 'center',
}

const textStyle = {
	margin: '0',
	padding: '5px 0',
	fontSize: '14px',
}

const socialIconsStyle = {
	marginTop: '20px',
}

const iconStyle = {
	color: '#ffffff',
	margin: '0 15px',
	fontSize: '24px',
	transition: 'transform 0.3s, color 0.3s',
}

const Footer = () => {
	return (
		<footer style={footerStyle}>
			<Container>
				<Row>
					<Col>
						<p style={textStyle}>Â© {new Date().getFullYear()} React E-Commerce. All rights reserved.</p>
						<p style={textStyle}>Contact us: contact@reactecommerce.com</p>
						<p style={textStyle}>Follow us on social media:</p>
						<div style={socialIconsStyle}>
							<a
								href='https://facebook.com'
								target='_blank'
								rel='noopener noreferrer'
								style={iconStyle}
								onMouseEnter={(e) => (e.currentTarget.style.color = '#007bff')}
								onMouseLeave={(e) => (e.currentTarget.style.color = '#ffffff')}
							>
								<i className='fab fa-facebook-f'></i>
							</a>
							<a
								href='https://twitter.com'
								target='_blank'
								rel='noopener noreferrer'
								style={iconStyle}
								onMouseEnter={(e) => (e.currentTarget.style.color = '#007bff')}
								onMouseLeave={(e) => (e.currentTarget.style.color = '#ffffff')}
							>
								<i className='fab fa-twitter'></i>
							</a>
							<a
								href='https://instagram.com'
								target='_blank'
								rel='noopener noreferrer'
								style={iconStyle}
								onMouseEnter={(e) => (e.currentTarget.style.color = '#007bff')}
								onMouseLeave={(e) => (e.currentTarget.style.color = '#ffffff')}
							>
								<i className='fab fa-instagram'></i>
							</a>
						</div>
					</Col>
				</Row>
			</Container>
		</footer>
	)
}

export default Footer
