import React, { useEffect } from 'react';

const AdminAnalytics = () => {
  // Demo data
  const data = {
    sales: [200, 400, 300, 700, 500, 800, 600], // Weekly sales
    orders: [10, 20, 15, 30, 25, 35, 40], // Weekly orders
    inventory: [50, 45, 30, 20, 60, 80, 70], // Inventory levels by day
    customerHistory: [
      { date: '2024-12-15', total: 120 },
      { date: '2024-12-16', total: 150 },
      { date: '2024-12-17', total: 100 },
    ], // Customer purchase history
    labels: ['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'],
  };

  useEffect(() => {
    // Draw Sales Chart
    const salesCanvas = document.getElementById('salesChart');
    const salesCtx = salesCanvas.getContext('2d');
    salesCtx.clearRect(0, 0, salesCanvas.width, salesCanvas.height);
    drawBarChart(salesCtx, data.sales, data.labels, 'Sales');

    // Draw Orders Chart
    const ordersCanvas = document.getElementById('ordersChart');
    const ordersCtx = ordersCanvas.getContext('2d');
    ordersCtx.clearRect(0, 0, ordersCanvas.width, ordersCanvas.height);
    drawLineChart(ordersCtx, data.orders, data.labels, 'Orders');

    // Draw Inventory Chart
    const inventoryCanvas = document.getElementById('inventoryChart');
    const inventoryCtx = inventoryCanvas.getContext('2d');
    inventoryCtx.clearRect(0, 0, inventoryCanvas.width, inventoryCanvas.height);
    drawBarChart(inventoryCtx, data.inventory, data.labels, 'Inventory');
  }, []);

  const drawBarChart = (ctx, dataset, labels, label) => {
    const maxVal = Math.max(...dataset);
    const canvasWidth = ctx.canvas.width;
    const canvasHeight = ctx.canvas.height;
    const barWidth = canvasWidth / dataset.length - 20;

    ctx.font = '16px Arial';
    ctx.fillStyle = '#333';
    ctx.textAlign = 'center';

    dataset.forEach((value, index) => {
      const barHeight = (value / maxVal) * (canvasHeight - 50);
      const x = index * (barWidth + 20) + 20;
      const y = canvasHeight - barHeight;

      ctx.fillStyle = '#ffc107';
      ctx.fillRect(x, y, barWidth, barHeight);

      ctx.fillStyle = '#333';
      ctx.fillText(value, x + barWidth / 2, y - 10);
      ctx.fillText(labels[index], x + barWidth / 2, canvasHeight - 10);
    });
  };

  const drawLineChart = (ctx, dataset, labels, label) => {
    const maxVal = Math.max(...dataset);
    const canvasWidth = ctx.canvas.width;
    const canvasHeight = ctx.canvas.height;
    const pointSpacing = canvasWidth / (dataset.length - 1);

    ctx.beginPath();
    ctx.strokeStyle = '#17a2b8';
    ctx.lineWidth = 2;

    dataset.forEach((value, index) => {
      const x = index * pointSpacing;
      const y = canvasHeight - (value / maxVal) * (canvasHeight - 50);

      if (index === 0) {
        ctx.moveTo(x, y);
      } else {
        ctx.lineTo(x, y);
      }
    });

    ctx.stroke();

    ctx.fillStyle = '#333';
    ctx.textAlign = 'center';

    dataset.forEach((value, index) => {
      const x = index * pointSpacing;
      const y = canvasHeight - (value / maxVal) * (canvasHeight - 50);

      ctx.beginPath();
      ctx.arc(x, y, 5, 0, Math.PI * 2);
      ctx.fillStyle = '#17a2b8';
      ctx.fill();

      ctx.fillStyle = '#333';
      ctx.fillText(value, x, y - 10);
      ctx.fillText(labels[index], x, canvasHeight - 10);
    });
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif', backgroundColor: '#f4f4f9' }}>
      <button
        onClick={() => window.history.back()}
        style={{
          backgroundColor: '#007bff',
          color: '#fff',
          padding: '10px 20px',
          border: 'none',
          borderRadius: '5px',
          cursor: 'pointer',
          marginBottom: '20px',
        }}
      >
        Quay Lại
      </button>

      <h1 style={{ textAlign: 'center', color: '#333', marginBottom: '30px' }}>Thống Kê Admin</h1>

      <div style={{ marginBottom: '40px' }}>
        <h2 style={{ color: '#007bff', textAlign: 'center' }}>Doanh Thu Hàng Tuần</h2>
        <canvas
          id="salesChart"
          width="600"
          height="400"
          style={{ border: '1px solid #ccc', display: 'block', margin: '20px auto' }}
        ></canvas>
      </div>

      <div style={{ marginBottom: '40px' }}>
        <h2 style={{ color: '#28a745', textAlign: 'center' }}>Số Lượng Đơn Hàng Hàng Tuần</h2>
        <canvas
          id="ordersChart"
          width="600"
          height="400"
          style={{ border: '1px solid #ccc', display: 'block', margin: '20px auto' }}
        ></canvas>
      </div>

      <div style={{ marginBottom: '40px' }}>
        <h2 style={{ color: '#ffc107', textAlign: 'center' }}>Tình Trạng Tồn Kho</h2>
        <canvas
          id="inventoryChart"
          width="600"
          height="400"
          style={{ border: '1px solid #ccc', display: 'block', margin: '20px auto' }}
        ></canvas>
      </div>

      <div>
        <h2 style={{ color: '#6c757d', textAlign: 'center' }}>Lịch Sử Mua Hàng Của Khách Hàng</h2>
        <table style={{ width: '80%', margin: '20px auto', borderCollapse: 'collapse' }}>
          <thead>
            <tr>
              <th style={{ border: '1px solid #ccc', padding: '10px', backgroundColor: '#f8f9fa' }}>Ngày</th>
              <th style={{ border: '1px solid #ccc', padding: '10px', backgroundColor: '#f8f9fa' }}>Tổng Giá Trị</th>
            </tr>
          </thead>
          <tbody>
            {data.customerHistory.map((entry, index) => (
              <tr key={index}>
                <td style={{ border: '1px solid #ccc', padding: '10px', textAlign: 'center' }}>{entry.date}</td>
                <td style={{ border: '1px solid #ccc', padding: '10px', textAlign: 'center' }}>${entry.total}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default AdminAnalytics;
