import React, { useEffect, useRef } from "react";

const Analytics = ({ data }) => {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");

    const chartData = [
      { label: "Người Dùng", value: data.totalUsers, color: "#4CAF50" },
      { label: "Sản Phẩm", value: data.totalProducts, color: "#FF9800" },
      { label: "Đơn Hàng", value: data.totalOrders, color: "#2196F3" },
      { label: "Doanh Thu", value: data.totalRevenue / 1000, color: "#F44336" }, // Scale doanh thu cho phù hợp
    ];

    const maxValue = Math.max(...chartData.map((item) => item.value));
    const canvasWidth = canvas.width;
    const canvasHeight = canvas.height;
    const padding = 40;
    const barWidth = (canvasWidth - padding * 2) / chartData.length;
    const chartHeight = canvasHeight - padding * 2;

    // Clear canvas
    ctx.clearRect(0, 0, canvasWidth, canvasHeight);

    // Draw Axes
    ctx.strokeStyle = "#000";
    ctx.beginPath();
    ctx.moveTo(padding, padding);
    ctx.lineTo(padding, canvasHeight - padding);
    ctx.lineTo(canvasWidth - padding, canvasHeight - padding);
    ctx.stroke();

    // Draw Bars
    chartData.forEach((item, index) => {
      const barHeight = (item.value / maxValue) * chartHeight;
      const x = padding + index * barWidth + barWidth * 0.1;
      const y = canvasHeight - padding - barHeight;

      // Draw bar
      ctx.fillStyle = item.color;
      ctx.fillRect(x, y, barWidth * 0.8, barHeight);

      // Draw labels
      ctx.fillStyle = "#000";
      ctx.font = "14px Arial";
      ctx.textAlign = "center";
      ctx.fillText(item.label, x + barWidth * 0.4, canvasHeight - padding + 20);
      ctx.fillText(
        item.value.toLocaleString(),
        x + barWidth * 0.4,
        y - 10
      );
    });
  }, [data]);

  return (
    <div style={{ textAlign: "center", margin: "20px" }}>
      <h1>Báo Cáo Thống Kê</h1>
      <canvas ref={canvasRef} width={600} height={400} style={{ border: "1px solid #ccc" }}></canvas>
    </div>
  );
};

export default Analytics;
