import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Analytics = () => {
  const [totalSales, setTotalSales] = useState(0);
  const [salesByBrand, setSalesByBrand] = useState([]);
  const [inventoryStats, setInventoryStats] = useState([]);
  const [customerPurchaseHistory, setCustomerPurchaseHistory] = useState([]);

  useEffect(() => {
    const fetchSalesStats = async () => {
      try {
        const { data } = await axios.get('/api/stats/sales');
        setTotalSales(data.totalSales);
        setSalesByBrand(data.salesByBrand);
      } catch (error) {
        console.error('Error fetching sales stats:', error);
      }
    };

    const fetchInventoryStats = async () => {
      try {
        const { data } = await axios.get('/api/stats/inventory');
        setInventoryStats(data);
      } catch (error) {
        console.error('Error fetching inventory stats:', error);
      }
    };

    const fetchCustomerPurchaseHistory = async () => {
      try {
        const { data } = await axios.get('/api/stats/customer-purchases');
        setCustomerPurchaseHistory(data);
      } catch (error) {
        console.error('Error fetching customer purchase history:', error);
      }
    };

    fetchSalesStats();
    fetchInventoryStats();
    fetchCustomerPurchaseHistory();
  }, []);

  return (
    <div className="analytics-container">
      <h1 className="analytics-title">Thống Kê Bán Hàng</h1>

      {/* Tổng sản phẩm bán được */}
      <div className="summary-section">
        <h2>Tổng Sản Phẩm Đã Bán:</h2>
        <p className="total-sales">{totalSales} sản phẩm</p>
      </div>

      {/* Thống kê theo hãng */}
      <div className="brand-stats-section">
        <h2>Thống Kê Theo Hãng</h2>
        <table className="brand-stats-table">
          <thead>
            <tr>
              <th>Hãng</th>
              <th>Số Lượng Bán</th>
            </tr>
          </thead>
          <tbody>
            {salesByBrand.map((item, index) => (
              <tr key={index}>
                <td>{item.brand}</td>
                <td>{item.sold}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Thống kê tồn kho */}
      <div className="inventory-stats-section">
        <h2>Thống Kê Tồn Kho</h2>
        <table className="inventory-stats-table">
          <thead>
            <tr>
              <th>Sản Phẩm</th>
              <th>Số Lượng Tồn Kho</th>
              <th>Tồn Kho Tính Đến Ngày</th>
            </tr>
          </thead>
          <tbody>
            {inventoryStats.map((item, index) => (
              <tr key={index}>
                <td>{item.product}</td>
                <td>{item.stock}</td>
                <td>{item.date}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Lịch sử mua hàng của khách hàng */}
      <div className="purchase-history-section">
        <h2>Lịch Sử Mua Hàng Của Khách Hàng</h2>
        <table className="purchase-history-table">
          <thead>
            <tr>
              <th>Tên Khách Hàng</th>
              <th>Sản Phẩm</th>
              <th>Số Lượng</th>
              <th>Ngày Mua</th>
            </tr>
          </thead>
          <tbody>
            {customerPurchaseHistory.map((item, index) => (
              <tr key={index}>
                <td>{item.customerName}</td>
                <td>{item.product}</td>
                <td>{item.quantity}</td>
                <td>{item.purchaseDate}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Analytics;
