import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Container, Row, Col } from 'react-bootstrap';
import ProductCarousel from '../components/ProductCarousel';
import Banner from '../components/Banner';
import Product from '../components/Product';
import Message from '../components/Message';
import Loader from '../components/Loader';
import Paginate from '../components/Paginate';
import Meta from '../components/Meta';

import { listProducts } from '../actions/productActions';

const HomeScreen = ({ match }) => {
  const keyword = match.params.keyword;
  const pageNumber = match.params.pageNumber || 1;

  const dispatch = useDispatch();

  const productList = useSelector((state) => state.productList);
  const { loading, error, products, page, pages } = productList;

  useEffect(() => {
    dispatch(listProducts(keyword, pageNumber));
  }, [dispatch, keyword, pageNumber]);

  return (
    <>
      <Meta />

      {/* Carousel */}
      {!keyword && <ProductCarousel />}

      {/* Featured Section */}
      <Container className="py-5">
        <Row className="text-center">
          <Col md={3}>
            <img src="/images/icon/icon-shipping.jpg" alt="Miễn phí giao hàng" width="50" />
            <h5 className="mt-2">Miễn phí giao hàng</h5>
            <p>Miễn phí vận chuyển tất cả các đơn hàng</p>
          </Col>
          <Col md={3}>
            <img src="/images/icon/icon-support.jpg" alt="Hỗ trợ 24/7" width="50" />
            <h5 className="mt-2">Hỗ trợ 24/7</h5>
            <p>Liên hệ bất kỳ lúc nào</p>
          </Col>
          <Col md={3}>
            <img src="/images/icon/icon-refund.jpg" alt="Hoàn tiền 100%" width="50" />
            <h5 className="mt-2">Hoàn tiền 100%</h5>
            <p>Bạn có 15 ngày để quay lại</p>
          </Col>
          <Col md={3}>
            <img src="/images/icon/icon-return.png" alt="30 ngày trả hàng" width="50" />
            <h5 className="mt-2">30 ngày trả hàng</h5>
            <p>Nếu sản phẩm có vấn đề</p>
          </Col>
        </Row>
      </Container>

      {/* Middle Banners */}
      <Banner position="middle" />

      {/* Best Sellers Section */}
      <Container className="py-5">
        <h2 className="text-center mb-4">Sản Phẩm Bán Chạy</h2>
        {loading ? (
          <Loader />
        ) : error ? (
          <Message variant="danger">{error}</Message>
        ) : (
          <Row>
            {products.slice(0, 4).map((product) => (
              <Col key={product._id} sm={12} md={6} lg={3}>
                <Product product={product} />
              </Col>
            ))}
          </Row>
        )}
      </Container>

      {/* All Products Section */}
      <Container className="py-5">
        <h2 className="text-center mb-4">Tất Cả Sản Phẩm</h2>
        {loading ? (
          <Loader />
        ) : error ? (
          <Message variant="danger">{error}</Message>
        ) : (
          <>
            <Row>
              {products.map((product) => (
                <Col key={product._id} sm={12} md={6} lg={4} xl={3}>
                  <Product product={product} />
                </Col>
              ))}
            </Row>
            <Paginate pages={pages} page={page} keyword={keyword || ''} />
          </>
        )}
      </Container>

      {/* Footer Banner */}
      <Banner position="footer" />
    </>
  );
};

export default HomeScreen;
