import React, { useEffect } from 'react';

const AdminAnalytics = () => {
  // Demo data
  const data = {
    sales: [200, 400, 300, 700, 500, 800, 600], // Weekly sales
    orders: [10, 20, 15, 30, 25, 35, 40], // Weekly orders
    labels: ['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'], // Days of the week
    inventory: {
      currentStock: 1200,
      stockAsOfDate: 1000,
    },
    products: [
      { id: 'P001', name: 'Product A', image: '/images/product-a.jpg', currentStock: 50, sold: 150 },
      { id: 'P002', name: 'Product B', image: '/images/product-b.jpg', currentStock: 30, sold: 70 },
      { id: 'P003', name: 'Product C', image: '/images/product-c.jpg', currentStock: 80, sold: 120 },
    ],
    purchaseHistory: [
      { customer: 'Alice', items: 3, total: '$50', date: '2024-12-18' },
      { customer: 'Bob', items: 5, total: '$120', date: '2024-12-19' },
      { customer: 'Charlie', items: 2, total: '$30', date: '2024-12-20' },
    ],
  };

  useEffect(() => {
    // Draw Sales Chart
    const salesCanvas = document.getElementById('salesChart');
    const salesCtx = salesCanvas.getContext('2d');

    salesCtx.clearRect(0, 0, salesCanvas.width, salesCanvas.height); // Clear canvas
    drawBarChart(salesCtx, data.sales, data.labels, 'Sales');

    // Draw Orders Chart
    const ordersCanvas = document.getElementById('ordersChart');
    const ordersCtx = ordersCanvas.getContext('2d');

    ordersCtx.clearRect(0, 0, ordersCanvas.width, ordersCanvas.height); // Clear canvas
    drawLineChart(ordersCtx, data.orders, data.labels, 'Orders');
  }, []);

  const drawBarChart = (ctx, dataset, labels, label) => {
    const maxVal = Math.max(...dataset);
    const canvasWidth = ctx.canvas.width;
    const canvasHeight = ctx.canvas.height;
    const barWidth = canvasWidth / dataset.length - 20;

    ctx.font = '16px Arial';
    ctx.fillStyle = '#333';
    ctx.textAlign = 'center';

    dataset.forEach((value, index) => {
      const barHeight = (value / maxVal) * (canvasHeight - 50); // Scale to canvas height
      const x = index * (barWidth + 20) + 20;
      const y = canvasHeight - barHeight;

      ctx.fillStyle = '#007bff';
      ctx.fillRect(x, y, barWidth, barHeight); // Draw bar

      ctx.fillStyle = '#333';
      ctx.fillText(value, x + barWidth / 2, y - 10); // Label above bar
      ctx.fillText(labels[index], x + barWidth / 2, canvasHeight - 10); // Label below bar
    });
  };

  const drawLineChart = (ctx, dataset, labels, label) => {
    const maxVal = Math.max(...dataset);
    const canvasWidth = ctx.canvas.width;
    const canvasHeight = ctx.canvas.height;
    const pointSpacing = canvasWidth / (dataset.length - 1);

    ctx.beginPath();
    ctx.strokeStyle = '#28a745';
    ctx.lineWidth = 2;

    dataset.forEach((value, index) => {
      const x = index * pointSpacing;
      const y = canvasHeight - (value / maxVal) * (canvasHeight - 50);

      if (index === 0) {
        ctx.moveTo(x, y);
      } else {
        ctx.lineTo(x, y);
      }
    });

    ctx.stroke();

    ctx.fillStyle = '#333';
    ctx.textAlign = 'center';

    dataset.forEach((value, index) => {
      const x = index * pointSpacing;
      const y = canvasHeight - (value / maxVal) * (canvasHeight - 50);

      ctx.beginPath();
      ctx.arc(x, y, 5, 0, Math.PI * 2); // Draw point
      ctx.fillStyle = '#28a745';
      ctx.fill();

      ctx.fillStyle = '#333';
      ctx.fillText(value, x, y - 10); // Label above point
      ctx.fillText(labels[index], x, canvasHeight - 10); // Label below point
    });
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif', backgroundColor: '#f4f4f9' }}>
      <button
        onClick={() => window.history.back()}
        style={{
          padding: '10px 15px',
          marginBottom: '20px',
          border: 'none',
          backgroundColor: '#007bff',
          color: '#fff',
          cursor: 'pointer',
          borderRadius: '5px',
        }}
      >
        Quay Lại
      </button>
      <h1 style={{ textAlign: 'center', color: '#333', marginBottom: '30px' }}>Thống Kê Admin</h1>

      <div style={{ marginBottom: '40px' }}>
        <h2 style={{ color: '#ffc107', textAlign: 'center' }}>Thống Kê Tồn Kho Hiện Tại</h2>
        <table style={{ width: '80%', margin: '20px auto', borderCollapse: 'collapse', border: '1px solid #ccc' }}>
          <thead>
            <tr>
              <th style={{ border: '1px solid #ccc', padding: '10px' }}>ID</th>
              <th style={{ border: '1px solid #ccc', padding: '10px' }}>Hình Ảnh</th>
              <th style={{ border: '1px solid #ccc', padding: '10px' }}>Số Lượng Tồn Kho</th>
            </tr>
          </thead>
          <tbody>
            {data.products.map((product, index) => (
              <tr key={index}>
                <td style={{ border: '1px solid #ccc', padding: '10px', textAlign: 'center' }}>{product.id}</td>
                <td style={{ border: '1px solid #ccc', padding: '10px', textAlign: 'center' }}>
                  <img
                    src={product.image}
                    alt={product.name}
                    style={{ maxWidth: '80px', height: 'auto', borderRadius: '5px' }}
                  />
                </td>
                <td style={{ border: '1px solid #ccc', padding: '10px', textAlign: 'center' }}>{product.currentStock}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default AdminAnalytics;
