import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { getUserDetails, updateUser } from '../actions/userActions';
import { USER_UPDATE_RESET } from '../constants/userConstants';

const UserScreen = ({ match, history }) => {
  const userId = match.params.id;

  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [isAdmin, setIsAdmin] = useState(false);

  const dispatch = useDispatch();

  const userDetails = useSelector((state) => state.userDetails);
  const { loading, error, user } = userDetails;

  const userUpdate = useSelector((state) => state.userUpdate);
  const { loading: loadingUpdate, error: errorUpdate, success: successUpdate } = userUpdate;

  useEffect(() => {
    if (successUpdate) {
      dispatch({ type: USER_UPDATE_RESET });
      history.push('/admin/userlist');
    } else {
      if (!user.name || user._id !== userId) {
        dispatch(getUserDetails(userId));
      } else {
        setName(user.name);
        setEmail(user.email);
        setIsAdmin(user.isAdmin);
      }
    }
  }, [dispatch, history, user, userId, successUpdate]);

  const submitHandler = (e) => {
    e.preventDefault();
    if (!name.trim() || !email.trim()) {
      alert('Tên và email không được để trống!');
      return;
    }
    dispatch(updateUser({ _id: userId, name, email, isAdmin }));
  };

  return (
    <div className="user-edit-screen">
      <Link to="/admin/userlist" className="back-button">
        &#8592; Quay Lại
      </Link>
      <div className="form-container">
        <h1>Chỉnh Sửa Thông Tin Người Dùng</h1>

        {loadingUpdate && <p>Đang cập nhật...</p>}
        {errorUpdate && <p className="error-message">{errorUpdate}</p>}
        {loading ? (
          <p>Đang tải...</p>
        ) : error ? (
          <p className="error-message">{error}</p>
        ) : (
          <form onSubmit={submitHandler} className="user-form">
            <div className="form-group">
              <label htmlFor="name">Tên</label>
              <input
                type="text"
                id="name"
                value={name}
                onChange={(e) => setName(e.target.value)}
                placeholder="Nhập tên"
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="email">Email</label>
              <input
                type="email"
                id="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="Nhập email"
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="isAdmin">
                <input
                  type="checkbox"
                  id="isAdmin"
                  checked={isAdmin}
                  onChange={(e) => setIsAdmin(e.target.checked)}
                />
                Quyền Quản Trị Viên
              </label>
            </div>
            <button type="submit" className="submit-button">
              Cập Nhật
            </button>
          </form>
        )}
      </div>
    </div>
  );
};

export default UserScreen;
