import React from 'react';
import { Link } from 'react-router-dom';
import { Card } from 'react-bootstrap';
import { FaBuilding, FaStar, FaShoppingCart } from 'react-icons/fa';
import Rating from './Rating';

const Product = ({ product }) => {
  return (
    <Card className="my-3 p-3 rounded product-card modern-card">
      {/* Product Image */}
      <div className="image-container">
        <Link to={`/product/${product._id}`} className="product-link">
          <Card.Img
            src={product.image}
            variant="top"
            className="product-img"
            alt={product.name}
          />
        </Link>
        <div className="overlay">
          <button className="add-to-cart-btn">
            <FaShoppingCart /> Thêm vào giỏ
          </button>
        </div>
      </div>

      {/* Product Content */}
      <Card.Body className="product-body">
        {/* Product Name */}
        <Link to={`/product/${product._id}`} className="product-link">
          <Card.Title as="h4" className="product-title modern-title">
            {product.name}
          </Card.Title>
        </Link>

        {/* Product Brand */}
        <Card.Text as="div" className="product-brand modern-brand">
          <FaBuilding className="icon" /> {product.brand}
        </Card.Text>

        {/* Product Rating */}
        <Card.Text as="div" className="product-rating">
          <FaStar className="icon" /> <Rating value={product.rating} />
        </Card.Text>

        {/* Product Price */}
        <Card.Text as="h3" className="product-price modern-price">
          {new Intl.NumberFormat('vi-VN').format(product.price)} VNĐ
        </Card.Text>
      </Card.Body>
    </Card>
  );
};

export default Product;
