import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Container, Row, Col } from 'react-bootstrap';
import ProductCarousel from '../components/ProductCarousel';
import Banner from '../components/Banner';
import Product from '../components/Product';
import Message from '../components/Message';
import Loader from '../components/Loader';
import Paginate from '../components/Paginate';
import Meta from '../components/Meta';
import { listProducts } from '../actions/productActions';

// Tách FeatureSection thành component riêng
const FeatureSection = () => (
  <Container className="py-5">
    <Row className="text-center">
      <Col md={3}>
        <img src="/images/icon/icon-shipping.jpg" alt="Miễn phí giao hàng" width="50" />
        <h5 className="mt-2">Miễn phí giao hàng</h5>
        <p>Miễn phí vận chuyển tất cả các đơn hàng</p>
      </Col>
      <Col md={3}>
        <img src="/images/icon/icon-support.jpg" alt="Hỗ trợ 24/7" width="50" />
        <h5 className="mt-2">Hỗ trợ 24/7</h5>
        <p>Liên hệ bất kỳ lúc nào</p>
      </Col>
      <Col md={3}>
        <img src="/images/icon/icon-refund.jpg" alt="Hoàn tiền 100%" width="50" />
        <h5 className="mt-2">Hoàn tiền 100%</h5>
        <p>Bạn có 15 ngày để quay lại</p>
      </Col>
      <Col md={3}>
        <img src="/images/icon/icon-return.png" alt="30 ngày trả hàng" width="50" />
        <h5 className="mt-2">30 ngày trả hàng</h5>
        <p>Nếu sản phẩm có vấn đề</p>
      </Col>
    </Row>
  </Container>
);

const HomeScreen = ({ match }) => {
  const keyword = match.params.keyword;
  const pageNumber = match.params.pageNumber || 1;

  const dispatch = useDispatch();

  const productList = useSelector((state) => state.productList);
  const { loading, error, products, page, pages } = productList;

  useEffect(() => {
    dispatch(listProducts(keyword, pageNumber));
  }, [dispatch, keyword, pageNumber]);

  // Component Title with Divider
  const SectionTitle = ({ children }) => (
    <div
      className="section-title text-center my-5"
      style={{
        position: 'relative',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
      }}
    >
      <hr
        style={{
          flex: 1,
          height: '2px',
          backgroundColor: '#FFD700',
          border: 'none',
        }}
      />
      <span
        style={{
          padding: '0 15px',
          fontSize: '24px',
          fontWeight: 'bold',
          backgroundColor: '#fff',
          textTransform: 'uppercase',
        }}
      >
        {children}
      </span>
      <hr
        style={{
          flex: 1,
          height: '2px',
          backgroundColor: '#FFD700',
          border: 'none',
        }}
      />
    </div>
  );

  return (
    <>
      <Meta />

      {/* Carousel */}
      {!keyword && <ProductCarousel />}

      {/* Featured Section */}
      <FeatureSection />



      {/* Top Banner Above Best Sellers Section */}
      <div className="top-banner">
        <img
          src="/images/banner/img2-middle-sinrato1.jpg"
          alt="Top Banner"
          style={{
            width: '50%',
            height: 'auto',
          }}
        />
      </div>
      <div className="top-banner">
        <img
          src="/images/banner/img2-middle-sinrato1.jpg"
          alt="Top Banner"
          style={{
            width: '50%',
            height: 'auto',
          }}
        />
      </div>

      {/* Best Sellers Section */}
      <Container className="py-5">
        <SectionTitle>Sản Phẩm Bán Chạy</SectionTitle>
        {loading ? (
          <Loader />
        ) : error ? (
          <Message variant="danger">{error}</Message>
        ) : products && products.length > 0 ? (
          <Row>
            {products.slice(0, 4).map((product) => (
              <Col key={product._id} sm={12} md={6} lg={3}>
                <Product product={product} />
              </Col>
            ))}
          </Row>
        ) : (
          <Message variant="info">Không có sản phẩm nào</Message>
        )}
      </Container>

      {/* Middle Banners */}
      <Banner position="middle" />

      {/* All Products Section */}
      <Container className="py-5">
        <SectionTitle>Tất Cả Sản Phẩm</SectionTitle>
        {loading ? (
          <Loader />
        ) : error ? (
          <Message variant="danger">{error}</Message>
        ) : products && products.length > 0 ? (
          <>
            <Row>
              {products.map((product) => (
                <Col key={product._id} sm={12} md={6} lg={4} xl={3}>
                  <Product product={product} />
                </Col>
              ))}
            </Row>
            <Paginate pages={pages} page={page} keyword={keyword || ''} />
          </>
        ) : (
          <Message variant="info">Không tìm thấy sản phẩm</Message>
        )}
      </Container>

      {/* Footer Banner */}
      <div className="footer-banner">
        <img
          src="/images/banner/img-bottom-sinrato1.jpg"
          alt="Footer Banner"
          style={{
            width: '100%',
            height: 'auto',
          }}
        />
      </div>
    </>
  );
};

export default HomeScreen;
