import React, { useState, useEffect } from 'react';
import { Row, Col, Image, Button } from 'react-bootstrap';
import { useDispatch, useSelector } from 'react-redux';
import Product from '../components/Product';
import Loader from '../components/Loader';
import Message from '../components/Message';
import { listProducts } from '../actions/productActions';

const StoreScreen = () => {
  const [priceRange, setPriceRange] = useState([0, 10000000]); // Giá mặc định từ 0 đến 10,000,000
  const [sortOption, setSortOption] = useState(''); // Lựa chọn sắp xếp

  const dispatch = useDispatch();

  const productList = useSelector((state) => state.productList);
  const { loading, error, products } = productList;

  useEffect(() => {
    dispatch(listProducts());
  }, [dispatch]);

  // Lọc sản phẩm theo giá
  const filteredProducts = products
    .filter(
      (product) => product.price >= priceRange[0] && product.price <= priceRange[1]
    )
    .sort((a, b) => {
      if (sortOption === 'name') {
        return a.name.localeCompare(b.name);
      } else if (sortOption === 'price') {
        return a.price - b.price;
      }
      return 0;
    });

  const handleSliderChange = (e, index) => {
    const newPriceRange = [...priceRange];
    newPriceRange[index] = parseInt(e.target.value, 10);
    setPriceRange(newPriceRange);
  };

  const handleSort = (option) => {
    setSortOption(option);
  };

  return (
    <Row>
      {/* Sidebar */}
      <Col md={3} className="mb-4">
        <div className="filter-section" style={{ padding: '20px', background: '#f9f9f9', borderRadius: '8px' }}>
          <h4 style={{ color: '#333' }}>Lọc Theo Giá</h4>
          <div style={{ marginBottom: '20px', textAlign: 'center', color: '#555' }}>
            <span>{priceRange[0].toLocaleString()} - {priceRange[1].toLocaleString()} VND</span>
          </div>
          <div style={{ position: 'relative', height: '40px', marginBottom: '20px' }}>
            <input
              type="range"
              min="0"
              max="20000000"
              step="500000"
              value={priceRange[0]}
              onChange={(e) => handleSliderChange(e, 0)}
              style={{
                position: 'absolute',
                width: '100%',
                zIndex: '2',
                background: 'transparent',
                accentColor: '#FFD700',
              }}
            />
            <input
              type="range"
              min="0"
              max="20000000"
              step="500000"
              value={priceRange[1]}
              onChange={(e) => handleSliderChange(e, 1)}
              style={{
                position: 'absolute',
                width: '100%',
                zIndex: '1',
                background: 'transparent',
                accentColor: '#FFD700',
              }}
            />
          </div>
          <Button variant="primary" className="mt-3" style={{ width: '100%' }}>
            Lọc
          </Button>
        </div>
      </Col>

      {/* Main Content */}
      <Col md={9}>
        {/* Banner */}
        <div style={{ marginBottom: '20px', position: 'relative' }}>
          <Image
            src="/images/banner/category-image.jpg" // Đường dẫn đến ảnh banner
            alt="Banner Cửa Hàng"
            fluid
            style={{ width: '100%', borderRadius: '8px' }}
          />
          <h1
            style={{
              position: 'absolute',
              top: '50%',
              left: '50%',
              transform: 'translate(-50%, -50%)',
              color: '#fff',
              textShadow: '2px 2px 8px rgba(0,0,0,0.7)',
              fontSize: '2rem',
            }}
          >
            Cửa Hàng
          </h1>
        </div>

        {/* Sort Buttons */}
        <div className="sort-buttons mb-4" style={{ textAlign: 'right' }}>
          <Button
            variant={sortOption === 'name' ? 'primary' : 'outline-primary'}
            className="me-2"
            onClick={() => handleSort('name')}
          >
            Tên
          </Button>
          <Button
            variant={sortOption === 'price' ? 'primary' : 'outline-primary'}
            onClick={() => handleSort('price')}
          >
            Giá ↑
          </Button>
        </div>

        {/* Product List */}
        {loading ? (
          <Loader />
        ) : error ? (
          <Message variant="danger">{error}</Message>
        ) : (
          <Row>
            {filteredProducts.map((product) => (
              <Col key={product._id} sm={12} md={6} lg={4} xl={3} className="mb-4">
                <Product product={product} />
              </Col>
            ))}
          </Row>
        )}
      </Col>
    </Row>
  );
};

export default StoreScreen;
