import React, { useEffect } from 'react';
import { LinkContainer } from 'react-router-bootstrap';
import { Table, Button, Image, OverlayTrigger, Tooltip } from 'react-bootstrap';
import { useDispatch, useSelector } from 'react-redux';
import Message from '../components/Message';
import Loader from '../components/Loader';
import { listOrders } from '../actions/orderActions';

const OrderListScreen = ({ history }) => {
  const dispatch = useDispatch();

  const orderList = useSelector((state) => state.orderList);
  const { loading, error, orders } = orderList;

  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;

  useEffect(() => {
    if (userInfo && userInfo.isAdmin) {
      dispatch(listOrders());
    } else {
      history.push('/login');
    }
  }, [dispatch, userInfo, history]);

  const renderTooltip = (text) => (
    <Tooltip id='tooltip'>{text}</Tooltip>
  );

  return (
    <>
      <h1>Đơn Hàng</h1>
      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant='danger'>{error}</Message>
      ) : (
        <Table bordered hover responsive className='table-sm'>
          <thead>
            <tr>
              <th>Ảnh</th>
              <th>ID</th>
              <th>User</th>
              <th>Ngày</th>
              <th>Tổng</th>
              <th>Đã Thanh Toán</th>
              <th>Đã Giao</th>
              <th>Chi Tiết</th>
            </tr>
          </thead>
          <tbody>
            {orders.map((order) => (
              <tr key={order._id}>
                {/* Avatar Column */}
                <td>
                  {order.user && order.user.avatar ? (
                    <Image
                      src={order.user.avatar}
                      alt={order.user.name}
                      roundedCircle
                      fluid
                      style={{ width: '40px', height: '40px' }}
                    />
                  ) : (
                    <i className='fas fa-user-circle' style={{ fontSize: '2rem', color: 'gray' }}></i>
                  )}
                </td>
                <td>{order._id}</td>
                <td>{order.user && order.user.name}</td>
                <td>{order.createdAt.substring(0, 10)}</td>
                <td>{order.totalPrice} VND</td>
                <td>
                  <OverlayTrigger
                    overlay={renderTooltip(
                      order.isPaid ? `Thanh toán ngày: ${order.paidAt.substring(0, 10)}` : 'Chưa thanh toán'
                    )}
                  >
                    <span>
                      {order.isPaid ? (
                        <i className='fas fa-check-circle' style={{ color: 'green' }}></i>
                      ) : (
                        <i className='fas fa-times-circle' style={{ color: 'red' }}></i>
                      )}
                    </span>
                  </OverlayTrigger>
                </td>
                <td>
                  <OverlayTrigger
                    overlay={renderTooltip(
                      order.isDelivered ? `Đã giao ngày: ${order.deliveredAt.substring(0, 10)}` : 'Chưa giao hàng'
                    )}
                  >
                    <span>
                      {order.isDelivered ? (
                        <i className='fas fa-check-circle' style={{ color: 'green' }}></i>
                      ) : (
                        <i className='fas fa-times-circle' style={{ color: 'red' }}></i>
                      )}
                    </span>
                  </OverlayTrigger>
                </td>
                <td>
                  <LinkContainer to={`/order/${order._id}`}>
                    <Button className='btn-sm' variant='light'>
                      Chi Tiết
                    </Button>
                  </LinkContainer>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      )}
    </>
  );
};

export default OrderListScreen;
