import React from 'react';

const Dashboard = () => {
  // Demo data for statistics
  const totalUsers = 120;
  const totalProducts = 340;
  const totalOrders = 85;
  const totalRevenue = 150000000; // In VND

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif', backgroundColor: '#f4f4f9' }}>
      <h1 style={{ textAlign: 'center', color: '#333', marginBottom: '30px' }}>Bảng Quản Trị</h1>

      {/* Statistics Section */}
      <div
        style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
          gap: '20px',
          marginBottom: '40px',
        }}
      >
        <div
          style={{
            backgroundColor: '#007bff',
            color: '#fff',
            padding: '20px',
            borderRadius: '10px',
            textAlign: 'center',
            transition: 'transform 0.2s',
          }}
          onMouseEnter={(e) => (e.target.style.transform = 'scale(1.05)')}
          onMouseLeave={(e) => (e.target.style.transform = 'scale(1)')}
        >
          <h2>{totalUsers}</h2>
          <p>Người Dùng</p>
          <button
            onClick={() => window.location.href = '/admin/userlist'}
            style={{
              backgroundColor: '#fff',
              color: '#007bff',
              padding: '10px 15px',
              border: 'none',
              borderRadius: '5px',
              cursor: 'pointer',
              marginTop: '10px',
            }}
          >
            Quản Lý
          </button>
        </div>

        <div
          style={{
            backgroundColor: '#28a745',
            color: '#fff',
            padding: '20px',
            borderRadius: '10px',
            textAlign: 'center',
            transition: 'transform 0.2s',
          }}
          onMouseEnter={(e) => (e.target.style.transform = 'scale(1.05)')}
          onMouseLeave={(e) => (e.target.style.transform = 'scale(1)')}
        >
          <h2>{totalProducts}</h2>
          <p>Sản Phẩm</p>
          <button
            onClick={() => window.location.href = '/admin/productlist'}
            style={{
              backgroundColor: '#fff',
              color: '#28a745',
              padding: '10px 15px',
              border: 'none',
              borderRadius: '5px',
              cursor: 'pointer',
              marginTop: '10px',
            }}
          >
            Quản Lý
          </button>
        </div>

        <div
          style={{
            backgroundColor: '#ffc107',
            color: '#fff',
            padding: '20px',
            borderRadius: '10px',
            textAlign: 'center',
            transition: 'transform 0.2s',
          }}
          onMouseEnter={(e) => (e.target.style.transform = 'scale(1.05)')}
          onMouseLeave={(e) => (e.target.style.transform = 'scale(1)')}
        >
          <h2>{totalOrders}</h2>
          <p>Đơn Hàng</p>
          <button
            onClick={() => window.location.href = '/admin/orderlist'}
            style={{
              backgroundColor: '#fff',
              color: '#ffc107',
              padding: '10px 15px',
              border: 'none',
              borderRadius: '5px',
              cursor: 'pointer',
              marginTop: '10px',
            }}
          >
            Quản Lý
          </button>
        </div>

        <div
          style={{
            backgroundColor: '#dc3545',
            color: '#fff',
            padding: '20px',
            borderRadius: '10px',
            textAlign: 'center',
            transition: 'transform 0.2s',
          }}
          onMouseEnter={(e) => (e.target.style.transform = 'scale(1.05)')}
          onMouseLeave={(e) => (e.target.style.transform = 'scale(1)')}
        >
          <h2>{totalRevenue.toLocaleString('vi-VN')} VND</h2>
          <p>Doanh Thu</p>
        </div>
      </div>

      {/* Quick Links Section */}
      <h2 style={{ textAlign: 'center', color: '#333', marginBottom: '20px' }}>Truy Cập Nhanh</h2>
      <div
        style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
          gap: '20px',
        }}
      >
        <div
          style={{
            backgroundColor: '#007bff',
            color: '#fff',
            padding: '20px',
            borderRadius: '10px',
            textAlign: 'center',
          }}
        >
          <p>Quản Lý Người Dùng</p>
          <button
            onClick={() => window.location.href = '/admin/userlist'}
            style={{
              backgroundColor: '#fff',
              color: '#007bff',
              padding: '10px 15px',
              border: 'none',
              borderRadius: '5px',
              cursor: 'pointer',
            }}
          >
            Đi Đến
          </button>
        </div>
        <div
          style={{
            backgroundColor: '#28a745',
            color: '#fff',
            padding: '20px',
            borderRadius: '10px',
            textAlign: 'center',
          }}
        >
          <p>Quản Lý Sản Phẩm</p>
          <button
            onClick={() => window.location.href = '/admin/productlist'}
            style={{
              backgroundColor: '#fff',
              color: '#28a745',
              padding: '10px 15px',
              border: 'none',
              borderRadius: '5px',
              cursor: 'pointer',
            }}
          >
            Đi Đến
          </button>
        </div>
        <div
          style={{
            backgroundColor: '#ffc107',
            color: '#fff',
            padding: '20px',
            borderRadius: '10px',
            textAlign: 'center',
          }}
        >
          <p>Quản Lý Đơn Hàng</p>
          <button
            onClick={() => window.location.href = '/admin/orderlist'}
            style={{
              backgroundColor: '#fff',
              color: '#ffc107',
              padding: '10px 15px',
              border: 'none',
              borderRadius: '5px',
              cursor: 'pointer',
            }}
          >
            Đi Đến
          </button>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
