import React from 'react';
import { Route } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { LinkContainer } from 'react-router-bootstrap';
import { Navbar, Nav, Container, NavDropdown } from 'react-bootstrap';
import SearchBox from './SearchBox';
import { logout } from '../actions/userActions';

const Header = () => {
  const dispatch = useDispatch();

  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;

  const logoutHandler = () => {
    dispatch(logout());
  };

  const topBarStyle = {
    backgroundColor: '#FFD700', // Chuyển thành vàng
    position: 'fixed',
    top: 0,
    left: 0,
    width: '100%',
    zIndex: 1100,
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'space-between',
    padding: '0.5rem 1.5rem',
    boxShadow: '0 1px 2px rgba(0, 0, 0, 0.1)',
  };

  const navbarStyle = {
    backgroundColor: '#FFD700', // Đồng bộ màu với Top Bar
    position: 'sticky',
    top: '60px',
    left: 0,
    width: '100%',
    zIndex: 1000,
    boxShadow: '0 2px 5px rgba(0, 0, 0, 0.1)',
  };


  const linkStyle = {
    color: '#111',
  };

  return (
    <header>
      {/* Top Bar: Logo and SearchBox */}
      <div style={topBarStyle}>
        <Container className="d-flex justify-content-between align-items-center">
          {/* Logo */}
          <LinkContainer to="/">
            <Navbar.Brand>
              <img
                src="/images/logo/logo.png"
                alt="Logo"
                style={{
                  maxHeight: '50px',
                  marginRight: '1rem', // Thêm khoảng cách giữa logo và ô tìm kiếm
                }}
              />
            </Navbar.Brand>
          </LinkContainer>

          {/* SearchBox */}
          <Route
            render={({ history }) => (
              <SearchBox
                history={history}
                style={{
                  maxWidth: '400px', // Giới hạn chiều rộng
                  width: '100%', // Đảm bảo responsive
                  marginLeft: '1rem', // Khoảng cách giữa các thành phần
                }}
              />
            )}
          />
        </Container>
      </div>

      {/* Navbar: Main Menu */}
      <Navbar style={navbarStyle} variant="light" expand="lg" collapseOnSelect>
        <Container>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="ml-auto">
              <LinkContainer to="/cart">
                <Nav.Link style={linkStyle}>
                  <i className="fas fa-shopping-cart"></i> GIỎ HÀNG
                </Nav.Link>
              </LinkContainer>
              {userInfo ? (
                <NavDropdown title={userInfo.name} id="username">
                  <LinkContainer to="/profile">
                    <NavDropdown.Item>THÔNG TIN CỦA TÔI</NavDropdown.Item>
                  </LinkContainer>
                  <NavDropdown.Item onClick={logoutHandler}>
                    ĐĂNG XUẤT
                  </NavDropdown.Item>
                </NavDropdown>
              ) : (
                <LinkContainer to="/login">
                  <Nav.Link style={linkStyle}>
                    <i className="fas fa-user"></i> ĐĂNG NHẬP
                  </Nav.Link>
                </LinkContainer>
              )}
              {userInfo && userInfo.isAdmin && (
                <NavDropdown title="Admin" id="adminmenu">
                  <LinkContainer to="/admin/userlist">
                    <NavDropdown.Item>NGƯỜI DÙNG</NavDropdown.Item>
                  </LinkContainer>
                  <LinkContainer to="/admin/productlist">
                    <NavDropdown.Item>CÁC SẢN PHẨM</NavDropdown.Item>
                  </LinkContainer>
                  <LinkContainer to="/admin/orderlist">
                    <NavDropdown.Item>ĐƠN HÀNG</NavDropdown.Item>
                  </LinkContainer>
                </NavDropdown>
              )}
              <LinkContainer to="/contact">
                <Nav.Link style={linkStyle}>LIÊN HỆ</Nav.Link>
              </LinkContainer>
              <LinkContainer to="/store">
                <Nav.Link style={linkStyle}>CỬA HÀNG</Nav.Link>
              </LinkContainer>
              <LinkContainer to="/about">
                <Nav.Link style={linkStyle}>VỀ CHÚNG TÔI</Nav.Link>
              </LinkContainer>
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>
    </header>
  );
};

export default Header;
