import React, { useState, useEffect } from 'react';
import { Row, Col, Image } from 'react-bootstrap';
import { useDispatch, useSelector } from 'react-redux';
import Slider from 'rc-slider';
import 'rc-slider/assets/index.css'; // Import CSS của thanh trượt
import Product from '../components/Product';
import Loader from '../components/Loader';
import Message from '../components/Message';
import { listProducts } from '../actions/productActions';

const StoreScreen = () => {
  const [priceRange, setPriceRange] = useState(1000); // Giá mặc định tối đa là 1000

  const dispatch = useDispatch();

  const productList = useSelector((state) => state.productList);
  const { loading, error, products } = productList;

  useEffect(() => {
    dispatch(listProducts());
  }, [dispatch]);

  // Lọc sản phẩm theo giá
  const filteredProducts = products.filter(
    (product) => product.price <= priceRange
  );

  return (
    <>
      {/* Banner */}
      <div style={{ marginBottom: '20px', position: 'relative' }}>
        <Image
          src="/images/banner/category-image.jpg" // Đường dẫn đến ảnh banner
          alt="Banner Cửa Hàng"
          fluid
          style={{ width: '100%', borderRadius: '8px' }}
        />
        <h1
          style={{
            position: 'absolute',
            top: '50%',
            left: '50%',
            transform: 'translate(-50%, -50%)',
            color: '#fff',
            textShadow: '2px 2px 8px rgba(0,0,0,0.7)',
            fontSize: '3rem',
          }}
        >
          Cửa Hàng
        </h1>
      </div>

      {/* Thanh trượt giá */}
      <Row className="mb-4">
        <Col md={12}>
          <h4>Lọc theo giá (đến): {priceRange} VND</h4>
          <Slider
            min={0}
            max={5000} // Giá tối đa
            step={100}
            value={priceRange}
            onChange={(value) => setPriceRange(value)}
            railStyle={{ backgroundColor: '#ddd' }} // Màu thanh trượt
            trackStyle={{ backgroundColor: '#FFD700' }} // Màu thanh được chọn
            handleStyle={{
              borderColor: '#FFD700',
              height: 20,
              width: 20,
              backgroundColor: '#111',
            }}
          />
        </Col>
      </Row>

      {/* Danh sách sản phẩm */}
      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant="danger">{error}</Message>
      ) : (
        <Row>
          {filteredProducts.map((product) => (
            <Col key={product._id} sm={12} md={6} lg={4} xl={3}>
              <Product product={product} />
            </Col>
          ))}
        </Row>
      )}
    </>
  );
};

export default StoreScreen;
