import React, { useEffect } from 'react';
import { useSelector } from 'react-redux';

const AdminAnalytics = () => {
  const productList = useSelector((state) => state.productList); // Lấy dữ liệu từ Redux Store
  const { products } = productList; // products từ state

  useEffect(() => {
    // Sales & Orders Chart logic...
  }, []);

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif', backgroundColor: '#f4f4f9' }}>
      <button
        onClick={() => window.history.back()}
        style={{
          padding: '10px 15px',
          marginBottom: '20px',
          border: 'none',
          backgroundColor: '#007bff',
          color: '#fff',
          cursor: 'pointer',
          borderRadius: '5px',
        }}
      >
        Quay Lại
      </button>
      <h1 style={{ textAlign: 'center', color: '#333', marginBottom: '30px' }}>Thống Kê Admin</h1>

      <div style={{ marginBottom: '40px' }}>
        <h2 style={{ color: '#ffc107', textAlign: 'center' }}>Thống Kê Tồn Kho Hiện Tại</h2>
        <table style={{ width: '80%', margin: '20px auto', borderCollapse: 'collapse', border: '1px solid #ccc' }}>
          <thead>
            <tr>
              <th style={{ border: '1px solid #ccc', padding: '10px' }}>ID Sản Phẩm</th>
              <th style={{ border: '1px solid #ccc', padding: '10px' }}>Hình Ảnh</th>
              <th style={{ border: '1px solid #ccc', padding: '10px' }}>Số Lượng Tồn Kho</th>
            </tr>
          </thead>
          <tbody>
            {products && products.map((product) => (
              <tr key={product._id}>
                <td style={{ border: '1px solid #ccc', padding: '10px', textAlign: 'center' }}>
                  {product._id}
                </td>
                <td style={{ border: '1px solid #ccc', padding: '10px', textAlign: 'center' }}>
                  <img
                    src={product.image}
                    alt={product.name}
                    style={{ maxWidth: '80px', height: 'auto', borderRadius: '5px' }}
                  />
                </td>
                <td style={{ border: '1px solid #ccc', padding: '10px', textAlign: 'center' }}>
                  {product.countInStock}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default AdminAnalytics;
