import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { Row, Col, Container, Button } from 'react-bootstrap';
import Product from '../components/Product';
import Message from '../components/Message';
import Loader from '../components/Loader';
import Paginate from '../components/Paginate';
import ProductCarousel from '../components/ProductCarousel';
import Meta from '../components/Meta';
import { listProducts } from '../actions/productActions';

const HomeScreen = ({ match }) => {
  const keyword = match.params.keyword;
  const pageNumber = match.params.pageNumber || 1;

  const dispatch = useDispatch();

  const productList = useSelector((state) => state.productList);
  const { loading, error, products, page, pages } = productList;

  useEffect(() => {
    dispatch(listProducts(keyword, pageNumber));
  }, [dispatch, keyword, pageNumber]);

  return (
    <>
      <Meta />
      {/* Product Carousel for Featured Products */}
      {!keyword ? (
        <div className="featured-section">
          <ProductCarousel />
        </div>
      ) : (
        <Container className="my-3">
          <Button as={Link} to="/" variant="outline-dark">
            <i className="fas fa-arrow-left"></i> Quay Lại Trang Chủ
          </Button>
        </Container>
      )}

      {/* Product Listing */}
      <Container>
        <h1 className="text-center my-4" style={{ fontWeight: 'bold' }}>
          {keyword ? `Kết quả tìm kiếm cho "${keyword}"` : 'Danh Sách Sản Phẩm'}
        </h1>

        {loading ? (
          <Loader />
        ) : error ? (
          <Message variant="danger">{error}</Message>
        ) : (
          <>
            <Row>
              {products.length > 0 ? (
                products.map((product) => (
                  <Col key={product._id} sm={12} md={6} lg={4} xl={3} className="mb-4">
                    <Product product={product} />
                  </Col>
                ))
              ) : (
                <Message variant="info">Không tìm thấy sản phẩm nào!</Message>
              )}
            </Row>

            {/* Pagination */}
            <div className="d-flex justify-content-center mt-4">
              <Paginate pages={pages} page={page} keyword={keyword ? keyword : ''} />
            </div>
          </>
        )}
      </Container>

      {/* Footer Call-to-Action */}
      <div className="cta-footer text-center py-4" style={{ background: '#f8f9fa' }}>
        <h2>Không Tìm Thấy Sản Phẩm Bạn Mong Muốn?</h2>
        <p>Hãy liên hệ với chúng tôi hoặc tiếp tục khám phá thêm!</p>
        <Button as={Link} to="/contact" variant="primary" className="mt-3">
          Liên Hệ Ngay
        </Button>
      </div>
    </>
  );
};

export default HomeScreen;
