import React, { useState, useEffect, useMemo } from 'react';
import { Row, Col, Image, Button, Form } from 'react-bootstrap';
import { useDispatch, useSelector } from 'react-redux';
import Product from '../components/Product';
import Loader from '../components/Loader';
import Message from '../components/Message';
import { listProducts } from '../actions/productActions';

const StoreScreen = () => {
  const [priceRange, setPriceRange] = useState({ min: 0, max: 20000000 });
  const [sortOption, setSortOption] = useState({ field: '', order: '' }); // Field: name/price, Order: asc/desc
  const [selectedBrands, setSelectedBrands] = useState([]);

  const dispatch = useDispatch();

  const productList = useSelector((state) => state.productList);
  const { loading, error, products } = productList;

  // Memoized unique brand list
  const brands = useMemo(
    () => [...new Set(products.map((product) => product.brand))],
    [products]
  );

  useEffect(() => {
    dispatch(listProducts());
  }, [dispatch]);

  // Filter and sort products
  const filteredProducts = useMemo(() => {
    const filtered = products
      .filter(
        (product) =>
          product.price >= priceRange.min &&
          product.price <= priceRange.max &&
          (selectedBrands.length === 0 || selectedBrands.includes(product.brand))
      )
      .sort((a, b) => {
        if (sortOption.field === 'name') {
          return sortOption.order === 'asc'
            ? a.name.localeCompare(b.name)
            : b.name.localeCompare(a.name);
        }
        if (sortOption.field === 'price') {
          return sortOption.order === 'asc' ? a.price - b.price : b.price - a.price;
        }
        return 0;
      });
    return filtered;
  }, [products, priceRange, selectedBrands, sortOption]);

  const handleSliderChange = (e) => {
    setPriceRange({ ...priceRange, max: parseInt(e.target.value, 10) });
  };

  const handleSort = (field) => {
    setSortOption((prev) => ({
      field,
      order: prev.order === 'asc' ? 'desc' : 'asc',
    }));
  };

  const handleBrandToggle = (brand) => {
    setSelectedBrands((prev) =>
      prev.includes(brand) ? prev.filter((b) => b !== brand) : [...prev, brand]
    );
  };

  const resetFilters = () => {
    setPriceRange({ min: 0, max: 20000000 });
    setSelectedBrands([]);
    setSortOption({ field: '', order: '' });
  };

  return (
    <Row>
      {/* Sidebar */}
      <Col md={3} className="mb-4">
        <div className="filter-section p-4 bg-light rounded">
          <h4>Lọc Theo Giá</h4>
          <div className="text-center mb-3">
            <span>Đến: {priceRange.max.toLocaleString()} VND</span>
          </div>
          <input
            type="range"
            min={priceRange.min}
            max="20000000"
            step="500000"
            value={priceRange.max}
            onChange={handleSliderChange}
            style={{
              width: '100%',
              accentColor: '#FFD700',
            }}
          />
          <Button variant="primary" className="mt-3 w-100" onClick={resetFilters}>
            Xóa Bộ Lọc
          </Button>
        </div>

        <div className="brand-section p-4 bg-light rounded mt-4">
          <h4>Lọc Theo Thương Hiệu</h4>
          {brands.map((brand) => (
            <Form.Check
              key={brand}
              type="checkbox"
              label={brand}
              checked={selectedBrands.includes(brand)}
              onChange={() => handleBrandToggle(brand)}
              className="mb-2"
            />
          ))}
        </div>

        <div className="mt-4 text-center">
          <Image
            src="/images/banner/img-module2.jpg"
            alt="Banner Phụ"
            fluid
            className="rounded mb-3"
          />
          <Image
            src="/images/banner/img-module3.jpg"
            alt="Banner Phụ"
            fluid
            className="rounded"
          />
        </div>
      </Col>

      {/* Main Content */}
      <Col md={9}>
        <div className="mb-4 position-relative">
          <Image
            src="/images/banner/category-image.jpg"
            alt="Banner Cửa Hàng"
            fluid
            className="rounded"
          />
          <h1
            style={{
              position: 'absolute',
              top: '50%',
              left: '50%',
              transform: 'translate(-50%, -50%)',
              color: '#fff',
              textShadow: '2px 2px 8px rgba(0,0,0,0.7)',
              fontSize: '2rem',
            }}
          >
            Cửa Hàng
          </h1>
        </div>

        <div className="d-flex justify-content-between align-items-center mb-4">
          <h4>Kết quả: {filteredProducts.length} sản phẩm</h4>
          <div>
            <Button
              variant={sortOption.field === 'name' ? 'primary' : 'outline-primary'}
              className="me-2"
              onClick={() => handleSort('name')}
            >
              Tên {sortOption.field === 'name' && (sortOption.order === 'asc' ? '↑' : '↓')}
            </Button>
            <Button
              variant={sortOption.field === 'price' ? 'primary' : 'outline-primary'}
              onClick={() => handleSort('price')}
            >
              Giá {sortOption.field === 'price' && (sortOption.order === 'asc' ? '↑' : '↓')}
            </Button>
          </div>
        </div>

        {loading ? (
          <Loader />
        ) : error ? (
          <Message variant="danger">{error}</Message>
        ) : (
          <Row>
            {filteredProducts.map((product) => (
              <Col key={product._id} sm={12} md={6} lg={4} xl={3} className="mb-4">
                <Product product={product} />
              </Col>
            ))}
          </Row>
        )}
      </Col>
    </Row>
  );
};

export default StoreScreen;
