import React from 'react';

const Analytics = () => {
  // Demo data (thay thế bằng API thực khi tích hợp)
  const totalSales = 1520; // Tổng sản phẩm bán được
  const salesByBrand = [
    { brand: 'Apple', sold: 540 },
    { brand: 'Samsung', sold: 320 },
    { brand: 'Sony', sold: 250 },
    { brand: 'Xiaomi', sold: 180 },
    { brand: 'Khác', sold: 230 },
  ];

  return (
    <div className="analytics-container">
      <h1 className="analytics-title">Thống Kê Bán Hàng</h1>

      {/* Tổng sản phẩm bán được */}
      <div className="summary-section">
        <h2>Tổng Sản Phẩm Đã Bán:</h2>
        <p className="total-sales">{totalSales} sản phẩm</p>
      </div>

      {/* Thống kê theo hãng */}
      <div className="brand-stats-section">
        <h2>Thống Kê Theo Hãng</h2>
        <table className="brand-stats-table">
          <thead>
            <tr>
              <th>Hãng</th>
              <th>Số Lượng Bán</th>
            </tr>
          </thead>
          <tbody>
            {salesByBrand.map((item, index) => (
              <tr key={index}>
                <td>{item.brand}</td>
                <td>{item.sold}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Analytics;
