import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { Table, Image, Button } from 'react-bootstrap';
import { listProducts } from '../actions/productActions';
import { listOrders } from '../actions/orderActions';
import Loader from '../components/Loader';
import Message from '../components/Message';

const AdminAnalytics = ({ history }) => {
  const dispatch = useDispatch();

  // Lấy dữ liệu sản phẩm
  const productList = useSelector((state) => state.productList);
  const { loading: loadingProducts, error: errorProducts, products } = productList;

  // Lấy dữ liệu đơn hàng
  const orderList = useSelector((state) => state.orderList);
  const { loading: loadingOrders, error: errorOrders, orders } = orderList;

  useEffect(() => {
    // Gửi yêu cầu lấy dữ liệu sản phẩm và đơn hàng
    dispatch(listProducts());
    dispatch(listOrders());
  }, [dispatch]);

  // Dữ liệu giả lập cho biểu đồ
  const chartData = {
    barChart: [
      { label: 'Sản phẩm A', value: 30 },
      { label: 'Sản phẩm B', value: 50 },
      { label: 'Sản phẩm C', value: 20 },
    ],
    pieChart: [
      { label: 'Đã giao', value: 60, color: '#4CAF50' },
      { label: 'Chưa giao', value: 40, color: '#F44336' },
    ],
  };

  // Tổng giá trị cho biểu đồ tròn
  const totalPie = chartData.pieChart.reduce((acc, item) => acc + item.value, 0);

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif', backgroundColor: '#f4f4f9' }}>
      {/* Nút Quay lại */}
      <Button
        variant="secondary"
        className="mb-3"
        onClick={() => history.goBack()}
      >
        <i className="fas fa-arrow-left"></i> Quay lại
      </Button>
      
      {/* Thống kê tồn kho */}
      <h1 style={{ textAlign: 'center', marginBottom: '30px' }}>Thống Kê Tồn Kho</h1>
      {loadingProducts ? (
        <Loader />
      ) : errorProducts ? (
        <Message variant="danger">{errorProducts}</Message>
      ) : (
        <Table bordered hover responsive className="table-sm" style={{ marginBottom: '50px', width: '90%', margin: 'auto' }}>
          <thead>
            <tr>
              <th>Hình Ảnh</th>
              <th>ID Sản Phẩm</th>
              <th>Tồn Kho</th>
            </tr>
          </thead>
          <tbody>
            {products.map((product) => (
              <tr key={product._id}>
                <td style={{ textAlign: 'center' }}>
                  <Image
                    src={product.image}
                    alt={product.name}
                    fluid
                    rounded
                    style={{ maxWidth: '80px' }}
                  />
                </td>
                <td style={{ textAlign: 'center' }}>{product._id}</td>
                <td style={{ textAlign: 'center' }}>{product.countInStock}</td>
              </tr>
            ))}
          </tbody>
        </Table>
      )}

      {/* Thống kê lịch sử mua hàng */}
      <h1 style={{ textAlign: 'center', margin: '50px 0 30px' }}>Thống Kê Lịch Sử Mua Hàng</h1>
      {loadingOrders ? (
        <Loader />
      ) : errorOrders ? (
        <Message variant="danger">{errorOrders}</Message>
      ) : (
        <Table bordered hover responsive className="table-sm" style={{ margin: 'auto', width: '90%' }}>
          <thead>
            <tr>
              <th>Hình Ảnh</th>
              <th>ID Đơn Hàng</th>
              <th>Tên Người Dùng</th>
              <th>Ngày Tạo</th>
            </tr>
          </thead>
          <tbody>
            {orders.map((order) => (
              <tr key={order._id}>
                <td style={{ textAlign: 'center' }}>
                  {order.orderItems && order.orderItems.length > 0 ? (
                    <Image
                      src={order.orderItems[0].image}
                      alt={order.orderItems[0].name}
                      fluid
                      rounded
                      style={{ width: '50px', height: '50px' }}
                    />
                  ) : (
                    <i
                      className="fas fa-box"
                      style={{ fontSize: '2rem', color: 'gray' }}
                    ></i>
                  )}
                </td>
                <td style={{ textAlign: 'center' }}>{order._id}</td>
                <td style={{ textAlign: 'center' }}>{order.user && order.user.name}</td>
                <td style={{ textAlign: 'center' }}>{order.createdAt.substring(0, 10)}</td>
              </tr>
            ))}
          </tbody>
        </Table>
      )}

      {/* Biểu đồ Thanh (Bar Chart) */}
      <h2 style={{ textAlign: 'center', marginTop: '50px' }}>Biểu Đồ Thanh Tồn Kho</h2>
      <div style={{ display: 'flex', justifyContent: 'center', marginTop: '20px' }}>
        <svg width="500" height="300">
          {chartData.barChart.map((item, index) => (
            <rect
              key={index}
              x={index * 120 + 50}
              y={300 - item.value * 5}
              width="100"
              height={item.value * 5}
              fill="#4CAF50"
            />
          ))}
        </svg>
      </div>
      
      {/* Biểu đồ Tròn (Pie Chart) */}
      <h2 style={{ textAlign: 'center', marginTop: '50px' }}>Biểu Đồ Tròn Đơn Hàng</h2>
      <div style={{ display: 'flex', justifyContent: 'center', marginTop: '20px' }}>
        <svg width="300" height="300" viewBox="0 0 32 32" style={{ transform: 'rotate(-90deg)' }}>
          {chartData.pieChart.map((slice, index) => {
            const radius = 15;
            const circumference = 2 * Math.PI * radius;
            const offset = circumference * (slice.value / totalPie);
            return (
              <circle
                key={index}
                cx="16"
                cy="16"
                r={radius}
                fill="transparent"
                stroke={slice.color}
                strokeWidth="32"
                strokeDasharray={circumference}
                strokeDashoffset={offset}
                transform="rotate(-90)"
              />
            );
          })}
        </svg>
      </div>
    </div>
  );
};

export default AdminAnalytics;
